import React, { useState, useEffect } from 'react';
import { nanoid } from 'nanoid';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import TableRowComponent from './TableRowComponent';
import { fetchOrders, createOrder, updateOrder, deleteOrder } from '../../utils/backendApi';

const TrackingStatus = () => {
  const columns = [
    "Номер заказа",
    "Статус",
    "Дата создания",
    "Номер телефона",
    "Сумма документа",
    "Полное имя",
    "Себестоимость"
  ];

  const initialOrder = {
    id: nanoid(),
    orderNumber: "",
    documentAmount: "",
    phoneNumber: "",
    fullName: "",
    costPrice: "",
    status: "",
    dateCreated: "",
    isSorted: false,
    isNew: true,
    isChanged: false
  };

  const [orders, setOrders] = useState([]);

  const getOrders = async () => {
    try {
      const fetchedOrders = await fetchOrders();
      const ordersWithIds = fetchedOrders.map(order => ({
        id: order.id || nanoid(),
        data: { ...order, isSorted: false, isNew: false, isChanged: false }
      }));
      setOrders(ordersWithIds);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  useEffect(() => {
    getOrders();
  }, []);

  const addRow = () => {
    setOrders([...orders, { id: nanoid(), data: initialOrder }]);
  };

  const saveRow = async (data, rowIndex) => {
    const isDuplicate = orders.some((order, index) => index !== rowIndex && order.data.orderNumber === data.orderNumber);
    if (isDuplicate) {
      setOrders((prevOrders) => {
        const newOrders = [...prevOrders];
        newOrders[rowIndex].data = { ...data, error: 'duplicate' };
        return newOrders;
      });
      return;
    }

    if (data.orderNumber.length !== 14 || !/^\d+$/.test(data.orderNumber)) {
      setOrders((prevOrders) => {
        const newOrders = [...prevOrders];
        newOrders[rowIndex].data = { ...data, error: 'invalid' };
        return newOrders;
      });
      return;
    }

    const { isSorted, isNew, error, isChanged, id, ...orderData } = data;

    try {
      if (isNew) {
        await createOrder({ id, ...orderData });
        const fetchedOrders = await fetchOrders();
        const ordersWithIds = fetchedOrders.map(order => ({
          id: order.id || nanoid(),
          data: { ...order, isSorted: false, isNew: false, isChanged: false }
        }));
        setOrders(ordersWithIds);
      } else if (isChanged) {
        await updateOrder(id, { id, ...orderData });

        // Fetch updated orders from the backend to ensure we have the latest data
        const fetchedOrders = await fetchOrders();
        const ordersWithIds = fetchedOrders.map(order => ({
          id: order.id || nanoid(),
          data: { ...order, isSorted: false, isNew: false, isChanged: false }
        }));
        setOrders(ordersWithIds);
      }
    } catch (error) {
      console.error('Error saving order:', error);
    }
  };

  const deleteRow = async (rowIndex, isLocalDelete) => {
    if (isLocalDelete) {
      setOrders((prevOrders) => prevOrders.filter((_, index) => index !== rowIndex));
      return;
    }

    const orderId = orders[rowIndex].id;

    try {
      await deleteOrder(orderId);
      const fetchedOrders = await fetchOrders();
      const ordersWithIds = fetchedOrders.map(order => ({
        id: order.id || nanoid(),
        data: { ...order, isSorted: false, isNew: false, isChanged: false }
      }));
      setOrders(ordersWithIds);
    } catch (error) {
      console.error('Error deleting order:', error);
    }
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell key="add-button-cell">
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order, index) => (
            <TableRowComponent
              key={order.id}
              id={order.id}
              data={order.data}
              onSave={(data) => saveRow({ ...data, id: order.id }, index)}
              onDelete={(isLocalDelete) => deleteRow(index, isLocalDelete)}
              isNew={order.data.isNew}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
