import React, { useState, useEffect, useCallback } from 'react';
import { TableRow, TableCell, Button } from '@mui/material';
import AdCostInput from './AdCostInputComponent';

const buttonStyle = {
  minWidth: '50px',
  padding: '4px 8px'
};

const ExpensesRowComponent = ({ id, data, onSave, onDelete }) => {

  const [inputs, setInputs] = useState({
    ...data
  });

  const [showAdCostError, setShowAdCostError] = useState(false);
  //const [showCostError, setShowCostError] = useState(false);

  useEffect(() => {
    setInputs({
      ...data
    });
  }, [data]);

  const handleInputChange = useCallback((key, value) => {
    const newInputs = { ...inputs, [key]: value, error: false};
    setInputs(newInputs);
    if (key === "documentSum") {
      setShowSumError(false);
    }
  }, [inputs]);

  const handleSave = () => {
    const { documentSum } = inputs;
    

    if (!/^\d+$/.test(documentSum)) {
      setShowAdCostError(true);
      return;
    }

    const { id, isNew } = inputs;
    const payload = { id, documentSum, isNew };
    onSave(payload);

    setShowAdCostError(false);
  };

  const handleDelete = () => {
    const isEmpty = Object.values(inputs).every(value => value === "" || value === false);
    if (isEmpty) {
      onDelete(true);
    } else {
      onDelete(false);
    }
  };

  return (
    <TableRow key={id}>
      <TableCell key={`documentSum-${id}`}>
        <AdCostInput
          value={inputs.documentSum}
          onChange={(value) => handleInputChange("documentSum", value)}
          showSumError={showSumError}
          errorMessage='Сумма должна быть целым положительным числом'
        />
      </TableCell>
      <TableCell key={`saveButton-${id}`}>
        <Button
          color="primary"
          variant="contained"
          onClick={handleSave}
          disabled={!data.isNew && !inputs.isChanged}
          style={buttonStyle}
        >
          {data.isNew ? "Записать" : "Обновить"}
        </Button>
      </TableCell>
      <TableCell key={`deleteButton-${id}`}>
        <Button color="secondary" variant="contained" onClick={handleDelete} style={buttonStyle}>
          Удалить
        </Button>
      </TableCell>
    </TableRow>
  );
};

export default ExpensesRowComponent;
