import React, { useState, useEffect } from 'react';
import { TableRow, TableCell, Button } from '@mui/material';
import OrderNumberInput from './OrderNumberInput';
import OrderStatus from './OrderStatus';
import DateCreated from './DateCreated';
import PhoneNumberInput from './PhoneNumberInput';
import DocumentAmountInput from './DocumentAmountInput';

const TableRowComponent = ({ id, data, onSave, onDelete, onSortChange }) => {
  const [inputs, setInputs] = useState({
    ...data,
    isSorted: data.isSorted || false,
    isStatusSorted: data.isStatusSorted || false,
    isDateSorted: data.isDateSorted || false,
    isAmountSorted: data.isAmountSorted || false,
    isPhoneSorted: data.isPhoneSorted || false
  });
  const [showError, setShowError] = useState(false);
  const [showPhoneError, setShowPhoneError] = useState(false);
  const [showAmountError, setShowAmountError] = useState(false);

  useEffect(() => {
    setInputs({
      ...data,
      isSorted: data.isSorted || false,
      isStatusSorted: data.isStatusSorted || false,
      isDateSorted: data.isDateSorted || false,
      isAmountSorted: data.isAmountSorted || false,
      isPhoneSorted: data.isPhoneSorted || false
    });
  }, [data]);

  const handleInputChange = (key, value) => {
    const newInputs = { ...inputs, [key]: value, error: false, isChanged: key !== "isSorted" && key !== "isStatusSorted" && key !== "isDateSorted" && key !== "isAmountSorted" && key !== "isPhoneSorted" };
    setInputs(newInputs);
    if (key === "phoneNumber") {
      const phoneNumberPattern = /^\(\d{3}\) \d{3}-\d{4}$/;
      const isPhoneValid = phoneNumberPattern.test(value);
      setShowPhoneError(!isPhoneValid);
    }
    if (key === "documentAmount") {
      const isAmountValid = /^\d+$/.test(value);
      setShowAmountError(!isAmountValid);
    }
  };

  const handleSortChange = (key, isSorted) => {
    handleInputChange(key, isSorted);
    onSortChange(id, key, isSorted);
  };

  const handleSave = () => {
    if (inputs.orderNumber.length !== 14 || !/^\d+$/.test(inputs.orderNumber)) {
      setShowError(true);
      return;
    }

    const phoneNumberPattern = /^\(\d{3}\) \d{3}-\d{4}$/;
    const isPhoneValid = phoneNumberPattern.test(inputs.phoneNumber);
    setShowPhoneError(!isPhoneValid);

    const isAmountValid = /^\d+$/.test(inputs.documentAmount);
    setShowAmountError(!isAmountValid);

    if (!isPhoneValid || !isAmountValid) {
      return;
    }
    
    const { id, orderNumber, documentAmount, phoneNumber, fullName, costPrice, status, dateCreated, isNew } = inputs;
    const payload = { id, orderNumber, documentAmount, phoneNumber, fullName, costPrice, status, dateCreated, isNew };
    onSave(payload);
  };

  const handleDelete = () => {
    const isEmpty = Object.values(inputs).every(value => value === "" || value === false);
    if (isEmpty) {
      onDelete(true);
    } else {
      onDelete(false);
    }
  };

  return (
    <TableRow key={id}>
      <TableCell key={`orderNumber-${id}`}>
        <OrderNumberInput
          value={inputs.orderNumber}
          onChange={(value) => handleInputChange("orderNumber", value)}
          isSorted={inputs.isSorted}
          onSortChange={(isSorted) => handleSortChange("isSorted", isSorted)}
          showError={showError && (inputs.orderNumber.length !== 14 || !/^\d+$/.test(inputs.orderNumber) || inputs.error === 'duplicate')}
          errorMessage={inputs.error === 'duplicate' ? 'Номер заказа уже существует' : 'Номер должен быть целым положительным 14-значным числом'}
        />
      </TableCell>
      <TableCell key={`status-${id}`}>
        <OrderStatus
          status={inputs.status}
          isSorted={inputs.isStatusSorted}
          onSortChange={(isSorted) => handleSortChange("isStatusSorted", isSorted)}
        />
      </TableCell>
      <TableCell key={`dateCreated-${id}`}>
        <DateCreated
          date={inputs.dateCreated}
          isSorted={inputs.isDateSorted}
          onSortChange={(isSorted) => handleSortChange("isDateSorted", isSorted)}
        />
      </TableCell>
      <TableCell key={`phoneNumber-${id}`}>
        <PhoneNumberInput
          value={inputs.phoneNumber}
          onChange={(value) => handleInputChange("phoneNumber", value)}
          isSorted={inputs.isPhoneSorted}
          onSortChange={(isSorted) => handleSortChange("isPhoneSorted", isSorted)}
          showError={showPhoneError && showError}
          errorMessage="Номер должен быть в формате (XXX) XXX-XXXX"
        />
      </TableCell>
      <TableCell key={`documentAmount-${id}`}>
        <DocumentAmountInput
          value={inputs.documentAmount}
          onChange={(value) => handleInputChange("documentAmount", value)}
          isSorted={inputs.isAmountSorted}
          onSortChange={(isSorted) => handleSortChange("isAmountSorted", isSorted)}
          showError={showAmountError && showError}
          errorMessage="Сумма документа должна быть положительным целым числом"
        />
      </TableCell>
      <TableCell key={`saveButton-${id}`}>
        <Button
          color="primary"
          variant="contained"
          onClick={handleSave}
          disabled={showPhoneError || showAmountError || (!data.isNew && !inputs.isChanged)}
        >
          {data.isNew ? "Записать" : "Обновить"}
        </Button>
      </TableCell>
      <TableCell key={`deleteButton-${id}`}>
        <Button color="secondary" variant="contained" onClick={handleDelete}>
          Удалить
        </Button>
      </TableCell>
    </TableRow>
  );
};

export default TableRowComponent;
