import React, { useState, useEffect } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { nanoid } from 'nanoid';
import TableRowComponent from './TableRowComponent';
import { fetchOrders, createOrder, updateOrder, deleteOrder } from '../../utils/backendApi';

const TrackingStatus = () => {
  const columns = [
    "Номер заказа",
    "Сумма документа",
    "Номер телефона",
    "Полное имя",
    "Себестоимость",
    "Статус",
    "Дата отправки"
  ];

  const [orders, setOrders] = useState([]);
  const [sortState, setSortState] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const fetchedOrders = await fetchOrders();
      setOrders(fetchedOrders);
    };

    fetchData();
  }, []);

  const addRow = () => {
    setOrders([...orders, { id: nanoid(), data: { orderNumber: "", documentAmount: "", phoneNumber: "", fullName: "", costPrice: "", status: "", dateCreated: "" } }]);
  };

  const saveRow = async (data) => {
    if (data.id) {
      const response = await updateOrder(data.id, data);
      setOrders(response);
    } else {
      const response = await createOrder(data);
      setOrders(response);
    }
  };

  const deleteRow = async (id, isLocal) => {
    if (isLocal) {
      setOrders(orders.filter(order => order.id !== id));
    } else {
      const response = await deleteOrder(id);
      setOrders(response);
    }
  };

  const handleSortChange = (id, key, isSorted) => {
    setSortState(prevState => ({
      ...prevState,
      [id]: {
        ...prevState[id],
        [key]: isSorted
      }
    }));
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order, index) => (
            <TableRowComponent
              key={order.id}
              id={order.id}
              data={order}
              onSave={saveRow}
              onDelete={(isLocal) => deleteRow(order.id, isLocal)}
              isNew={!order.orderNumber}
              onSortChange={handleSortChange}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
