import React, { useState, useEffect, useCallback } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { fetchApi } from '../../utils/backendApi';
import DisplayMetric from './DisplayMetric';

const DisplayMetrics = () => {
  const columns = ["Сумма всех заказов", "Прибыль доставленных", "Затраты", "Сумма документа", "Номер телефона", "Себестоимость"];
  const [orders, setOrders] = useState([]);
  const [costs, setCosts] = useState([]);
  const [metrics, setMetrics] = useState([]);
  const [totalCosts, setTotalCosts] = useState(0);

  const fetchOrdersUrl = "http://localhost:4000/orders";
  const fetchExpensesUrl = "http://localhost:4000/expenses";

  useEffect(() => {
    const fetchData = async () => {
      const [fetchedOrders, fetchedCosts] = await Promise.all([
        fetchApi(fetchOrdersUrl, 'GET'),
        fetchApi(fetchExpensesUrl, 'GET')
      ]);

      setOrders(fetchedOrders);
      setCosts(fetchedCosts);
    };

    fetchData();
  }, []);

  const sumAllValues = useCallback((dataArray) => {
    if (dataArray.length === 0) return 0; // Проверка на пустой массив

    const totalSum = dataArray.reduce((totalSum, obj) => {
      const { id, date, ...rest } = obj;
      const sum = Object.values(rest).reduce((acc, value) => acc + Number(value), 0);
      return totalSum + sum;
    }, 0);

    return totalSum;
  }, []);

  useEffect(() => {
    const total = sumAllValues(costs);
    setTotalCosts(total);
    if (total !== 0) { // Проверка на ненулевую сумму
      setMetrics(prevMetrics => [total, ...prevMetrics]);
    }
  }, [costs, sumAllValues]);

  const calculateSumOfDifferences = useCallback((dataArray) => {
    if (dataArray.length === 0) return; // Проверка на пустой массив

    const totalSum = dataArray.reduce((totalSum, obj) => {
      if (obj.status === 'Відправлення отримано. Грошовий переказ видано одержувачу.' || obj.status === 'Відправлення отримано.') {
        const documentSum = Number(obj.documentSum);
        const cost = Number(obj.cost);
        const difference = documentSum - cost;
        return totalSum + difference;
      }
      return totalSum;
    }, 0);

    const netSum = totalSum - totalCosts;

    if (netSum !== 0) { // Проверка на ненулевую сумму
      setMetrics(prevMetrics => [netSum, ...prevMetrics]);
    }
  }, [totalCosts]);

  useEffect(() => {
    calculateSumOfDifferences(orders);
  }, [orders, totalCosts, calculateSumOfDifferences]);

  const calculateTotalDocumentSum = useCallback((dataArray) => {
    if (dataArray.length === 0) return 0;
    return dataArray.reduce((total, obj) => {
      const documentSum = Number(obj.documentSum);
      return total + documentSum;
    }, 0);
  }, []);

  useEffect(() => {
    const totalDocumentSum = calculateTotalDocumentSum(orders);
    if (totalDocumentSum && totalDocumentSum !== 0) { // Проверка на ненулевую сумму
      setMetrics(prevMetrics => [totalDocumentSum, ...prevMetrics]);
    }
  }, [orders, calculateTotalDocumentSum]);

  console.log(metrics);

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          <TableRow>
            {metrics.map((metric, index) => (
              <TableCell key={index}>
                <DisplayMetric value={metric} />
              </TableCell>
            ))}
          </TableRow>
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default DisplayMetrics;
