import React from 'react';
import { TextField, FormControlLabel, Checkbox } from '@mui/material';

const DocumentAmountInput = ({ value, onChange, isSorted, onSortChange, showError, errorMessage }) => {
  const handleChange = (e) => {
    const newValue = e.target.value;
    if (/^\d*$/.test(newValue)) {
      onChange(newValue);
    }
  };

  const handleSortChange = (e) => {
    onSortChange(e.target.checked);
  };

  return (
    <div>
      <TextField
        value={value}
        onChange={handleChange}
        variant="outlined"
        size="small"
        fullWidth
        placeholder="Сумма документа"
        error={showError}
        helperText={showError ? errorMessage : ''}
      />
      <FormControlLabel
        control={<Checkbox checked={isSorted} onChange={handleSortChange} />}
        label="Сортировка по значению"
      />
    </div>
  );
};

export default DocumentAmountInput;
