import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, Button, Box, Checkbox } from '@mui/material';
import InputMask from 'react-input-mask';

const TrackingStatus = () => {
  const initialOrder = {
    id: null,
    Number: '',
    DateCreated: '',
    Status: '',
    DocumentCost: '',
    PhoneRecipient: '',
    RecipientFullName: '',
    CostOfGoods: '',
    checked: {
      Number: false,
      DocumentCost: false,
      PhoneRecipient: false,
      RecipientFullName: false,
      CostOfGoods: false,
    },
  };

  const [orders, setOrders] = useState([initialOrder]);
  const [errors, setErrors] = useState({});
  const [phoneErrors, setPhoneErrors] = useState({});
  const [costErrors, setCostErrors] = useState({});
  const [nameErrors, setNameErrors] = useState({});
  const [costOfGoodsErrors, setCostOfGoodsErrors] = useState({});

  // ...

  const handleCheckboxChange = (id, field) => {
    const updatedOrders = orders.map((order) =>
      order.id === id ? { ...order, checked: { ...order.checked, [field]: !order.checked[field] } } : order
    );
    setOrders(updatedOrders);
  };

  // ...

  return (
    <>
      <TableContainer component={Paper} style={{ marginBottom: '150px' }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Номер ЕН</TableCell>
              <TableCell>Сумма</TableCell>
              <TableCell>Номер телефона</TableCell>
              <TableCell>Имя</TableCell>
              <TableCell>Себестоимость</TableCell>
              <TableCell>Дата отправки</TableCell>
              <TableCell>Статус отправки</TableCell>
              <TableCell>
                <Button onClick={addOrder} variant="contained" color="primary" style={{ width: '100px' }}>
                  Добавить
                </Button>
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map((order) => (
              <TableRow key={order.id || 'new'}>
                <TableCell>
                  <Checkbox
                    checked={order.checked.Number}
                    onChange={() => handleCheckboxChange(order.id, 'Number')}
                  />
                  <TextField
                    value={order.Number}
                    onChange={(e) => handleNumberChange(order.id, e.target.value)}
                    placeholder="Введите номер"
                    error={!!errors[order.id]}
                    helperText={errors[order.id]}
                  />
                </TableCell>
                <TableCell>
                  <Checkbox
                    checked={order.checked.DocumentCost}
                    onChange={() => handleCheckboxChange(order.id, 'DocumentCost')}
                  />
                  <TextField
                    value={order.DocumentCost}
                    onChange={(e) => handleCostChange(order.id, e.target.value)}
                    placeholder="Введите сумму"
                    error={!!costErrors[order.id]}
                    helperText={costErrors[order.id]}
                  />
                </TableCell>
                <TableCell>
                  <Checkbox
                    checked={order.checked.PhoneRecipient}
                    onChange={() => handleCheckboxChange(order.id, 'PhoneRecipient')}
                  />
                  <InputMask
                    mask="+380 99 999 99 99"
                    maskChar="_"
                    value={order.PhoneRecipient}
                    onChange={(e) => handlePhoneChange(order.id, e.target.value)}
                  >
                    {(inputProps) => (
                      <TextField
                        {...inputProps}
                        placeholder="Введите номер телефона"
                        error={!!phoneErrors[order.id]}
                        helperText={phoneErrors[order.id]}
                      />
                    )}
                  </InputMask>
                </TableCell>
                <TableCell>
                  <Checkbox
                    checked={order.checked.RecipientFullName}
                    onChange={() => handleCheckboxChange(order.id, 'RecipientFullName')}
                  />
                  <TextField
                    value={order.RecipientFullName}
                    onChange={(e) => handleNameChange(order.id, e.target.value)}
                    placeholder="Введите имя"
                    error={!!nameErrors[order.id]}
                    helperText={nameErrors[order.id]}
                  />
                </TableCell>
                <TableCell>
                  <Checkbox
                    checked={order.checked.CostOfGoods}
                    onChange={() => handleCheckboxChange(order.id, 'CostOfGoods')}
                  />
                  <TextField
                    value={order.CostOfGoods}
                    onChange={(e) => handleCostOfGoodsChange(order.id, e.target.value)}
                    placeholder="Введите себестоимость"
                    error={!!costOfGoodsErrors[order.id]}
                    helperText={costOfGoodsErrors[order.id]}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={order.DateCreated || ''}
                    placeholder="-"
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={order.Status || ''}
                    placeholder="-"
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </TableCell>
                <TableCell>
                  <Button
                    onClick={() => handleSaveOrUpdate(order.id)}
                    variant="contained"
                    color="primary"
                    style={{ width: '100px' }}
                    disabled={isButtonDisabled(order.id)}
                  >
                    {order.id ? 'Обновить' : 'Записать'}
                  </Button>
                </TableCell>
                {order.id && (
                  <TableCell>
                    <Button onClick={() => removeOrder(order.id)} variant="contained" color="secondary" style={{ width: '100px' }}>
                      Удалить
                    </Button>
                  </TableCell>
                )}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* ... */}
    </>
  );
};

export default TrackingStatus;
