import React, { useState, useEffect, useCallback } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { nanoid } from 'nanoid';
import ExpensesRowComponent from './ExpensesRowComponent';
import { fetchApi } from '../../utils/backendApi';

const ExpensesTable = () => {
  const columns = ["Дата", "Реклама", "Затраты на возврат", "Затраты на брак", "Затраты на хостинг", "Другие затраты"];
  const initialCosts = {
    id: nanoid(),
    date: "",
    advertising: "",
    returnCosts: "",
    defectCosts: "",
    hostingCosts: "", 
    otherCosts: "",
    isNew: true
  };

  const [costs, setCosts] = useState([]);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const fetchExpensesUrl = "http://localhost:4000/expenses";


  useEffect(() => {
    const fetchData = async () => {
      const fetchedCosts = await fetchApi(fetchExpensesUrl, 'GET');
      setCosts(fetchedCosts);
    };
  
    fetchData();
  }, []);
  

  const addRow = () => {
    setCosts([...costs, { ...initialCosts, isNew: true }]);
  };

  const saveRow = useCallback(async (data) => {
    const { id, date, advertising, returnCosts, defectCosts, hostingCosts, otherCosts, isNew } = data;
    const payload = { id, date, advertising, returnCosts, defectCosts, hostingCosts, otherCosts };

    if (isNew) {
      const response = await fetchApi(fetchExpensesUrl, 'POST', null, payload );
      setCosts(response);
    } else {
      const response = await fetchApi(fetchExpensesUrl, 'PATCH', id, payload );
      setCosts(response);
    }
  }, [setCosts]);

  const deleteRow = useCallback(async (id, isNew) => {
    if (isNew) {
      setCosts(costs => costs.filter(cost => cost.id !== id));
    } else {
      const response = await fetchApi(fetchExpensesUrl, 'DELETE', id );
      setCosts(response);
    }
  }, [setCosts]);

  const filteredCosts = costs.filter(cost => {
    const costDate = new Date(cost.date);
    const isWithinDateRange =
    (!startDate || costDate >= new Date(startDate)) &&
    (!endDate || costDate <= new Date(endDate));   
    return (
      isWithinDateRange 
    );
  });

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}{column === "Дата" ? (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                  <input
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                    style={{ display: "block", marginTop: "8px" }}
                  />
                  <input
                    type="date"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                    style={{ display: "block", marginTop: "8px" }}
                  />
                </div>
              ) : (
                ""
              )}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {filteredCosts.map((cost) => (
            <ExpensesRowComponent
              key={cost.id}
              id={cost.id}
              data={{ ...cost, isNew: cost.isNew }}
              onSave={saveRow}
              onDelete={(isNew) => deleteRow(cost.id, isNew)}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default ExpensesTable;
