import React from 'react';
import { TextField, FormControlLabel, Checkbox } from '@mui/material';

const OrderNumberInput = ({ value, onChange, isSorted, onSortChange }) => {
  const handleChange = (e) => {
    const newValue = e.target.value;
    const isValid = /^\d{0,14}$/.test(newValue); // Проверка, что ввод только чисел и не более 14 цифр
    if (isValid) {
      onChange(newValue);
    }
  };

  const handleSortChange = (e) => {
    onSortChange(e.target.checked);
  };

  const isError = value.length !== 14 || !/^\d+$/.test(value);

  return (
    <div>
      <TextField
        value={value}
        onChange={handleChange}
        variant="outlined"
        size="small"
        fullWidth
        placeholder="Номер заказа"
        error={isError}
        helperText={isError ? 'Номер должен быть целым положительным 14-значным числом' : ''}
      />
      <FormControlLabel
        control={<Checkbox checked={isSorted} onChange={handleSortChange} />}
        label="Сортировка по значению"
      />
    </div>
  );
};

export default OrderNumberInput;
