import React, { useState, useEffect } from 'react';
import { TableRow, TableCell, Button } from '@mui/material';
import OrderNumberInput from './OrderNumberInput';

const TableRowComponent = ({ data, onSave, onDelete, isNew }) => {
  const [inputs, setInputs] = useState(data);
  const [showError, setShowError] = useState(false);

  useEffect(() => {
    setInputs(data);
  }, [data]);

  const handleInputChange = (key, value) => {
    const newInputs = { ...inputs, [key]: value, error: false, isChanged: true }; // Помечаем строку как измененную
    setInputs(newInputs);
  };

  const handleSortChange = (isSorted) => {
    handleInputChange("isSorted", isSorted);
  };

  const handleSave = () => {
    setShowError(true);
    onSave(inputs);
  };

  const handleDelete = () => {
    onDelete();
  };

  return (
    <TableRow>
      <TableCell>
        <OrderNumberInput
          value={inputs.orderNumber}
          onChange={(value) => handleInputChange("orderNumber", value)}
          isSorted={inputs.isSorted}
          onSortChange={handleSortChange}
          showError={showError && (inputs.orderNumber.length !== 14 || !/^\d+$/.test(inputs.orderNumber) || inputs.error === 'duplicate')}
          errorMessage={inputs.error === 'duplicate' ? 'Номер заказа уже существует' : 'Номер должен быть целым положительным 14-значным числом'}
        />
      </TableCell>
      {Object.keys(inputs).slice(1, -4).map((key, index) => (
        <TableCell key={index + 1}>
          {inputs[key] !== null && inputs[key] !== undefined ? String(inputs[key]) : ""}
        </TableCell>
      ))}
      <TableCell>
        <Button 
          color="primary" 
          variant="contained" 
          onClick={handleSave} 
          disabled={!isNew && !inputs.isChanged} // Активируем кнопку только для новых или измененных строк
        >
          {isNew ? "Записать" : "Обновить"}
        </Button>
      </TableCell>
      <TableCell>
        <Button color="secondary" variant="contained" onClick={handleDelete}>
          Удалить
        </Button>
      </TableCell>
    </TableRow>
  );
};

export default TableRowComponent;
