import React, { useState, useEffect } from 'react';
import { TableRow, TableCell, Button } from '@mui/material';
import OrderNumberInput from './OrderNumberInput';
import OrderStatus from './OrderStatus';
import DateCreated from './DateCreated';
import PhoneNumberInput from './PhoneNumberInput';
import DocumentAmountInput from './DocumentAmountInput';
import CostPriceInput from './CostPriceInput';
import FullNameInput from './FullNameInput';

const buttonStyle = {
  minWidth: '50px', // Устанавливаем минимальную ширину кнопок
  padding: '4px 8px' // Уменьшаем отступы внутри кнопок
};

const TableRowComponent = ({ id, data, onSave, onDelete, onSortChange }) => {
  const [inputs, setInputs] = useState({
    ...data,
    isSorted: data.isSorted || false,
    isStatusSorted: data.isStatusSorted || false,
    isDateSorted: data.isDateSorted || false,
    isAmountSorted: data.isAmountSorted || false,
    isPhoneSorted: data.isPhoneSorted || false,
    isCostSorted: data.isCostSorted || false,
    isFullNameSorted: data.isFullNameSorted || false
  });
  const [showError, setShowError] = useState(false);
  const [showPhoneError, setShowPhoneError] = useState(false);
  const [showAmountError, setShowAmountError] = useState(false);
  const [showCostError, setShowCostError] = useState(false);
  const [showFullNameError, setShowFullNameError] = useState(false);

  useEffect(() => {
    setInputs({
      ...data,
      isSorted: data.isSorted || false,
      isStatusSorted: data.isStatusSorted || false,
      isDateSorted: data.isDateSorted || false,
      isAmountSorted: data.isAmountSorted || false,
      isPhoneSorted: data.isPhoneSorted || false,
      isCostSorted: data.isCostSorted || false,
      isFullNameSorted: data.isFullNameSorted || false
    });
  }, [data]);

  const handleInputChange = (key, value) => {
    const newInputs = { ...inputs, [key]: value, error: false, isChanged: key !== "isSorted" && key !== "isStatusSorted" && key !== "isDateSorted" && key !== "isAmountSorted" && key !== "isPhoneSorted" && key !== "isCostSorted" && key !== "isFullNameSorted" };
    setInputs(newInputs);
    if (key === "phoneNumber") {
      const phoneNumberPattern = /^\(\d{3}\) \d{3}-\d{4}$/;
      const isPhoneValid = phoneNumberPattern.test(value);
      setShowPhoneError(!isPhoneValid);
    }
    if (key === "documentAmount") {
      const isAmountValid = /^\d+$/.test(value);
      setShowAmountError(!isAmountValid);
    }
    if (key === "costPrice") {
      const isCostValid = /^\d+$/.test(value);
      setShowCostError(!isCostValid);
    }
    if (key === "fullName") {
      const isFullNameValid = /^[a-zA-Zа-яА-ЯёЁ\s]+$/.test(value);
      setShowFullNameError(!isFullNameValid);
    }
  };

  const handleSortChange = (key, isSorted, sortKey, componentValue) => {
    handleInputChange(key, isSorted);
    onSortChange(id, key, isSorted, sortKey, componentValue);
  };

  const handleSave = () => {
    if (inputs.orderNumber.length !== 14 || !/^\d+$/.test(inputs.orderNumber)) {
      setShowError(true);
      return;
    }

    const phoneNumberPattern = /^\(\d{3}\) \d{3}-\d{4}$/;
    const isPhoneValid = phoneNumberPattern.test(inputs.phoneNumber);
    setShowPhoneError(!isPhoneValid);

    const isAmountValid = /^\d+$/.test(inputs.documentAmount);
    setShowAmountError(!isAmountValid);

    const isCostValid = /^\d+$/.test(inputs.costPrice);
    setShowCostError(!isCostValid);

    const isFullNameValid = /^[a-zA-Zа-яА-ЯёЁ\s]+$/.test(inputs.fullName);
    setShowFullNameError(!isFullNameValid);

    if (!isPhoneValid || !isAmountValid || !isCostValid || !isFullNameValid) {
      return;
    }
    
    const { id, orderNumber, documentAmount, phoneNumber, fullName, costPrice, status, dateCreated, isNew } = inputs;
    const payload = { id, orderNumber, documentAmount, phoneNumber, fullName, costPrice, status, dateCreated };
    onSave(payload);
  };

  const handleDelete = () => {
    const isEmpty = Object.values(inputs).every(value => value === "" || value === false);
    if (isEmpty) {
      onDelete(true);
    } else {
      onDelete(false);
    }
  };

  return (
    <TableRow key={id}>
      <TableCell key={`orderNumber-${id}`}>
        <OrderNumberInput
          value={inputs.orderNumber}
          onChange={(value) => handleInputChange("orderNumber", value)}
          isSorted={inputs.isSorted}
          onSortChange={(isSorted) => handleSortChange("isSorted", isSorted, 'orderNumber', inputs.orderNumber)}
          showError={showError && (inputs.orderNumber.length !== 14 || !/^\d+$/.test(inputs.orderNumber) || inputs.error === 'duplicate')}
          errorMessage={inputs.error === 'duplicate' ? 'Номер заказа уже существует' : 'Номер должен быть целым положительным 14-значным числом'}
        />
      </TableCell>
      <TableCell key={`status-${id}`}>
        <OrderStatus
          status={inputs.status}
          isSorted={inputs.isStatusSorted}
          onSortChange={(isSorted) => handleSortChange("isStatusSorted", isSorted, 'status', inputs.status)}
        />
      </TableCell>
      <TableCell key={`dateCreated-${id}`}>
        <DateCreated
          date={inputs.dateCreated}
          isSorted={inputs.isDateSorted}
          onSortChange={(isSorted) => handleSortChange("isDateSorted", isSorted, 'dateCreated', inputs.dateCreated)}
        />
      </TableCell>
      <TableCell key={`phoneNumber-${id}`}>
        <PhoneNumberInput
          value={inputs.phoneNumber}
          onChange={(value) => handleInputChange("phoneNumber", value)}
          isSorted={inputs.isPhoneSorted}
          onSortChange={(isSorted) => handleSortChange("isPhoneSorted", isSorted, 'phoneNumber', inputs.phoneNumber)}
          showError={showPhoneError && showError}
          errorMessage="Номер должен быть в формате (XXX) XXX-XXXX"
        />
      </TableCell>
      <TableCell key={`documentAmount-${id}`}>
        <DocumentAmountInput
          value={inputs.documentAmount}
          onChange={(value) => handleInputChange("documentAmount", value)}
          isSorted={inputs.isAmountSorted}
          onSortChange={(isSorted) => handleSortChange("isAmountSorted", isSorted, 'documentAmount', inputs.documentAmount)}
          showError={showAmountError && showError}
          errorMessage="Сумма документа должна быть положительным целым числом"
        />
      </TableCell>
      <TableCell key={`costPrice-${id}`}>
        <CostPriceInput
          value={inputs.costPrice}
          onChange={(value) => handleInputChange("costPrice", value)}
          isSorted={inputs.isCostSorted}
          onSortChange={(isSorted) => handleSortChange("isCostSorted", isSorted, 'costPrice', inputs.costPrice)}
          showError={showCostError && showError}
          errorMessage="Себестоимость должна быть положительным целым числом"
        />
      </TableCell>
      <TableCell key={`fullName-${id}`}>
        <FullNameInput
          value={inputs.fullName}
          onChange={(value) => handleInputChange("fullName", value)}
          isSorted={inputs.isFullNameSorted}
          onSortChange={(isSorted) => handleSortChange("isFullNameSorted", isSorted, 'fullName', inputs.fullName)}
          showError={showFullNameError && showError}
          errorMessage="Полное имя должно содержать только буквы"
        />
      </TableCell>
      <TableCell key={`saveButton-${id}`}>
        <Button
          color="primary"
          variant="contained"
          onClick={handleSave}
          disabled={showPhoneError || showAmountError || showCostError || showFullNameError || (!data.isNew && !inputs.isChanged)}
          style={buttonStyle} // Применение стилей к кнопке
        >
          {data.isNew ? "Записать" : "Обновить"}
        </Button>
      </TableCell>
      <TableCell key={`deleteButton-${id}`}>
        <Button
          color="secondary"
          variant="contained"
          onClick={handleDelete}
          style={buttonStyle} // Применение стилей к кнопке
        >
          Удалить
        </Button>
      </TableCell>
    </TableRow>
  );
};

export default TableRowComponent;
