import React, { useEffect } from 'react';
import { TextField, FormControlLabel, Checkbox } from '@mui/material';
import InputMask from 'react-input-mask';

const PhoneNumberInput = ({ value, onChange, showError, errorMessage, onValidityChange }) => {
  // Обработчик изменения значения ввода
  const handleChange = (e) => {
    onChange(e.target.value); // Обновляем значение
  };

  // Эффект для проверки валидности номера телефона при изменении значения
  useEffect(() => {
    // Регулярное выражение для проверки полного формата номера телефона
    const phoneNumberPattern = /^\+38 \(\d{3}\) \d{3}-\d{4}$/;
    // Проверка валидности номера
    const isPhoneValid = phoneNumberPattern.test(value);
    // Передача валидности номера в родительский компонент
    onValidityChange(isPhoneValid);
  }, [value, onValidityChange]); // Выполнение эффекта при изменении value или onValidityChange

  return (
    <div>
      <InputMask
        mask="+38 (099) 999-9999" // Маска для ввода номера телефона
        value={value}
        onChange={handleChange}
      >
        {(inputProps) => (
          <TextField
            {...inputProps} // Передача всех свойств TextField
            variant="outlined"
            size="small"
            fullWidth
            placeholder="Номер телефона"
            error={showError} // Отображение ошибки, если есть
            helperText={showError ? errorMessage : ''} // Текст ошибки, если есть
          />
        )}
      </InputMask>
      <FormControlLabel
        control={<Checkbox />}
        label="Сортировка"
      />
    </div>
  );
};

export default PhoneNumberInput;
