import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { nanoid } from 'nanoid';
import ExpensesRowComponent from './ExpensesRowComponent';
import { fetchOrders, createOrder, updateOrder, deleteOrder } from '../../utils/backendApi';

const ExpensesTable = () => {
  const columns = ["Дата", "Реклама", "Затраты на возврат", "Затраты на брак", "Затраты на хостинг", "Другие затраты"];
  const initialOrder = {
    id: nanoid(),
    date: "",
    ad: "",
    returnExpense: "",
    damageExpense: "",
    host: "", 
    documentCost: "",
    isNew: true
  };

  const [orders, setOrders] = useState([]);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");


  useEffect(() => {
    const fetchData = async () => {
      const fetchedOrders = await fetchOrders();
      setOrders(fetchedOrders.map(order => ({
        ...order,
        checkedStatuses: order.checkedStatuses || { isStatusChecked: false, isDateCreatedChecked: false, isDocumentSumChecked: false, isPhoneNumberChecked: false, isDocumentCostChecked: false }
      })));
    };

    fetchData();
  }, []);

  const existingOrderNumbers = useMemo(() => orders.map(order => order.orderNumber), [orders]);

  const addRow = () => {
    setOrders([...orders, { ...initialOrder, isNew: true, checkedStatuses: { isStatusChecked: false, isDateCreatedChecked: false, isDocumentSumChecked: false, isPhoneNumberChecked: false, isDocumentCostChecked: false } }]);
  };

  const saveRow = useCallback(async (data) => {
    const { id, orderNumber, status, dateCreated, documentSum, phoneNumber, documentCost, isNew } = data;
    const payload = { id, orderNumber, status, dateCreated, documentSum, phoneNumber, documentCost };

    if (isNew) {
      const response = await createOrder(payload);
      setOrders(response);
    } else {
      const response = await updateOrder(id, payload);
      setOrders(response);
    }
  }, [setOrders]);

  const deleteRow = useCallback(async (id, isLocal) => {
    if (isLocal) {
      setOrders(orders => orders.filter(order => order.id !== id));
    } else {
      const response = await deleteOrder(id);
      setOrders(response);
    }
  }, [setOrders]);

  const handleSortChange = (sortKey, isSorted, filterValue, id) => {
    setOrders(orders.map(order => 
      order.id === id ? { ...order, checkedStatuses: { ...order.checkedStatuses, [sortKey]: isSorted } } : order
    ));

    if (sortKey === "isStatusChecked" && isSorted) {
      setFilterStatus(filterValue);
    } else if (sortKey === "isDateCreatedChecked" && isSorted) {
      setFilterDate(filterValue);
    }else if (sortKey === "isDocumentSumChecked" && isSorted) {
      setFilterDocumentSum(filterValue);
    } else if (sortKey === "isPhoneNumberChecked" && isSorted){
      setFilterPhoneNumber(filterValue);
    } else if (sortKey === "isDocumentCostChecked" && isSorted) {
      setFilterDocumentCost(filterValue);
    } else {
      setFilterPhoneNumber("");
      setFilterStatus("");
      setFilterDate("");
      setFilterDocumentSum("");
      setFilterDocumentCost("");
    }
  };

  const filteredOrders = orders.filter(order => {
    const orderDate = new Date(order.dateCreated);
    const isWithinDateRange =
    (!startDate || orderDate >= new Date(startDate)) &&
    (!endDate || orderDate <= new Date(endDate));   

    return (
      isWithinDateRange &&
      (!filterStatus || order.status === filterStatus) &&
      (!filterDate || order.dateCreated === filterDate)&&
      (!filterPhoneNumber || order.phoneNumber === filterPhoneNumber)&&
      (!filterDocumentCost || order.documentCost === filterDocumentCost)&&
      (!filterDocumentSum || order.documentSum === filterDocumentSum)
    );
  });

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}{column === "Дата отправки" ? (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                  <input
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                    style={{ display: "block", marginTop: "8px" }}
                  />
                  <input
                    type="date"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                    style={{ display: "block", marginTop: "8px" }}
                  />
                </div>
              ) : (
                ""
              )}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {filteredOrders.map((order) => (
            <ExpensesRowComponent
              key={order.id}
              id={order.id}
              data={{ ...order, isNew: order.isNew }}
              existingOrderNumbers={existingOrderNumbers}
              onSave={saveRow}
              onDelete={(isLocal) => deleteRow(order.id, isLocal)}
              onSortChange={handleSortChange}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default ExpensesTable;
