import React from 'react';
import { TextField } from '@mui/material';
import { FormControlLabel, Checkbox } from '@mui/material';

const DocumentSumInput = ({ value, onChange, showError, errorMessage, isSorted, onSortChange  }) => {
  const handleChange = (e) => {
    const newValue = e.target.value;
    onChange(newValue);
  };

  return (
    <div>
      <TextField
        value={value}
        onChange={handleChange}
        variant="outlined"
        size="small"
        fullWidth
        placeholder="Сумма заказа"
        error={showError}
        helperText={showError ? errorMessage : ''}
        inputProps={{ maxLength: 14 }} // Ограничение на количество символов
        style={{ width: '170px' }} // Увеличиваем ширину на 10%
      />
      <FormControlLabel
        control={
          <Checkbox
            checked={isSorted}
            onChange={handleCheckboxChange}
          />
        }
        label="Сортировка"
      />
    </div>
  );
};

export default DocumentSumInput;
