import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Typography, Box } from '@mui/material';

const Register = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistered, setIsRegistered] = useState(false);

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:4000/users/register', { username, email, password });
      if (response.status === 201) { // Проверяем успешность регистрации
        setIsRegistered(true);
      }
    } catch (err) {
      alert('Ошибка при регистрации пользователя');
    }
  };

  return (
    <div>
      <Typography variant="h4" component="h2" gutterBottom>
        Register
      </Typography>
      {isRegistered ? (
        <Typography variant="body1" color="primary">
          Вы успешно зарегистрировались! Теперь войдите на сайт.
        </Typography>
      ) : (
        <Box component="form" sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
          <TextField
            label="Username"
            variant="outlined"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <TextField
            label="Email"
            variant="outlined"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            label="Password"
            variant="outlined"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button variant="contained" color="primary" onClick={handleRegister}>
            Register
          </Button>
        </Box>
      )}
    </div>
  );
};

export default Register;
