import React, { useState } from 'react';
import axios from 'axios';

const TrackingStatus = () => {
  const [trackingNumber, setTrackingNumber] = useState('');
  const [status, setStatus] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchStatus = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('https://api.novaposhta.ua/v2.0/json/', {
        apiKey: 'YOUR_API_KEY', // замени на свой API ключ
        modelName: 'TrackingDocument',
        calledMethod: 'getStatusDocuments',
        methodProperties: {
          Documents: [
            {
              DocumentNumber: trackingNumber,
              Phone: ''
            }
          ]
        }
      });

      setStatus(response.data.data[0]);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setTrackingNumber(e.target.value);
  };

  return (
    <div>
      <h2>Статус отправления</h2>
      <input 
        type="text" 
        value={trackingNumber} 
        onChange={handleInputChange} 
        placeholder="Введите номер посылки"
      />
      <button onClick={fetchStatus}>Получить статус</button>
      {loading && <p>Загрузка...</p>}
      {error && <p>Ошибка: {error}</p>}
      {status && (
        <div>
          <p>Статус: {status.Status}</p>
          <p>Дата: {status.DocumentWeight}</p>
          <p>Описание: {status.StatusCode}</p>
        </div>
      )}
    </div>
  );
};

export default TrackingStatus;
