import React, { useState, useEffect, useCallback } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { fetchApi } from '../../utils/backendApi';
import DisplayMetric from './DisplayMetric';

const DisplayMetrics = () => {
  const columns = ["Сумма всех заказов", "Прибыль доставленных", "Затраты", "Сумма документа", "Номер телефона", "Себестоимость"];
  const [orders, setOrders] = useState([]);
  const [costs, setCosts] = useState([]);
  const [metrics, setMetrics] = useState([]);

  const fetchOrdersUrl = "http://localhost:4000/orders";
  const fetchExpensesUrl = "http://localhost:4000/expenses";

  useEffect(() => {
    const fetchData = async () => {
      const [fetchedOrders, fetchedCosts] = await Promise.all([
        fetchApi(fetchOrdersUrl, 'GET'),
        fetchApi(fetchExpensesUrl, 'GET')
      ]);

      setOrders(fetchedOrders);
      setCosts(fetchedCosts);
    };

    fetchData();
  }, []);

  const sumAllValues = useCallback((dataArray) => {
    if (dataArray.length === 0) return 0; // Проверка на пустой массив

    return dataArray.reduce((totalSum, obj) => {
      const { id, date, ...rest } = obj;
      const sum = Object.values(rest).reduce((acc, value) => acc + Number(value), 0);
      return totalSum + sum;
    }, 0);
  }, []);

  const calculateProfit = useCallback((ordersArray, costsArray) => {
    if (ordersArray.length === 0 || costsArray.length === 0) return; // Проверка на пустые массивы

    // Фильтруем заказы по статусу
    const filteredOrders = ordersArray.filter(order =>
      order.status === 'Відправлення отримано. Грошовий переказ видано одержувачу.' || order.status === 'Відправлення отримано.'
    );

    // Сумма всех заказов
    const totalOrdersSum = filteredOrders.reduce((total, order) => {
      const documentSum = Number(order.documentSum);
      return total + documentSum;
    }, 0);

    // Сумма всех себестоимостей заказов
    const totalCostOfOrders = filteredOrders.reduce((total, order) => {
      const cost = Number(order.cost);
      return total + cost;
    }, 0);

    // Сумма всех расходов
    const totalCosts = sumAllValues(costsArray);

    // Общая себестоимость
    const totalOverallCost = totalCostOfOrders + totalCosts;

    // Прибыль
    const profit = totalOrdersSum - totalOverallCost;

    setMetrics([totalOrdersSum, profit, totalCosts, totalOverallCost]);
  }, [sumAllValues]);

  useEffect(() => {
    calculateProfit(orders, costs);
  }, [orders, costs, calculateProfit]);

  console.log(metrics);

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          <TableRow>
            {metrics.map((metric, index) => (
              <TableCell key={index}>
                <DisplayMetric value={metric} />
              </TableCell>
            ))}
          </TableRow>
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default DisplayMetrics;
