import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { fetchApi } from '../../utils/backendApi';

const DisplayMetrics = () => {
  const columns = ["Номер заказа", "Статус", "Дата отправки", "Сумма документа", "Номер телефона", "Себестоимость"];
  

  const fetchOrdersUrl = "http://localhost:4000/metrics";

  const [metrics, setMetrics] = useState([]);


  useEffect(() => {
    const fetchData = async () => {
      const fetchedOrders = await fetchApi(fetchOrdersUrl, 'GET');
      setOrders(fetchedOrders.map(order => ({
        ...order,
        checkedStatuses: order.checkedStatuses || { isStatusChecked: false, isDateCreatedChecked: false, isDocumentSumChecked: false, isPhoneNumberChecked: false, isDocumentCostChecked: false }
      })));
    };

    fetchData();
  }, []);


  const deleteRow = useCallback(async (id, isNew) => {
    if (isNew) {
      setOrders(orders => orders.filter(order => order.id !== id));
    } else {
      const response = await fetchApi(fetchOrdersUrl, 'DELETE', id);
      setOrders(response);
    }
  }, [setOrders]);

  const handleSortChange = (sortKey, isSorted, filterValue, id) => {
    setOrders(orders.map(order => 
      order.id === id ? { ...order, checkedStatuses: { ...order.checkedStatuses, [sortKey]: isSorted } } : order
    ));

    if (sortKey === "isStatusChecked" && isSorted) {
      setFilterStatus(filterValue);
    } else if (sortKey === "isDateCreatedChecked" && isSorted) {
      setFilterDate(filterValue);
    }else if (sortKey === "isDocumentSumChecked" && isSorted) {
      setFilterDocumentSum(filterValue);
    } else if (sortKey === "isPhoneNumberChecked" && isSorted){
      setFilterPhoneNumber(filterValue);
    } else if (sortKey === "isDocumentCostChecked" && isSorted) {
      setFilterDocumentCost(filterValue);
    } else {
      setFilterPhoneNumber("");
      setFilterStatus("");
      setFilterDate("");
      setFilterDocumentSum("");
      setFilterDocumentCost("");
    }
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default DisplayMetrics;
