import React from 'react';
import { TextField, FormControlLabel, Checkbox } from '@mui/material';

const CostPriceInput = ({ value, onChange, isSorted, onSortChange, showError, errorMessage }) => {
  const handleChange = (e) => {
    onChange(e.target.value);
  };

  const handleSortChange = (e) => {
    onSortChange(e.target.checked);
  };

  return (
    <div>
      <TextField
        value={value}
        onChange={handleChange}
        variant="outlined"
        size="small"
        fullWidth
        placeholder="Себестоимость"
        error={showError}
        helperText={showError ? errorMessage : ''}
        inputProps={{ style: { width: '70px' } }} // Уменьшение ширины поля ввода на 30%
      />
      <FormControlLabel
        control={<Checkbox checked={isSorted} onChange={handleSortChange} />}
        label="Сортировка"
      />
    </div>
  );
};

export default CostPriceInput;
