import React, { useState, useEffect } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { nanoid } from 'nanoid';
import TableRowComponent from './TableRowComponent';
import { fetchOrders, createOrder, updateOrder, deleteOrder } from '../../utils/backendApi';

const TrackingStatus = () => {
  const columns = [
    "Номер заказа",
    "Статус",
    "Дата отправки",
    "Номер телефона",
    "Сумма документа",
    "Полное имя",
    "Себестоимость"
  ];

  const initialOrder = {
    id: nanoid(),
    orderNumber: "",
    documentAmount: "",
    phoneNumber: "",
    fullName: "",
    costPrice: "",
    status: "",
    dateCreated: "",
    isNew: true
  };

  const [orders, setOrders] = useState([]);
  const [sortCriteria, setSortCriteria] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const fetchedOrders = await fetchOrders();
      setOrders(fetchedOrders);
    };

    fetchData();
  }, []);

  const addRow = () => {
    setOrders([...orders, { ...initialOrder }]);
  };

  const saveRow = async (data) => {
    const { id, orderNumber, documentAmount, phoneNumber, fullName, costPrice, status, dateCreated, isNew } = data;
    const payload = { id, orderNumber, documentAmount, phoneNumber, fullName, costPrice, status, dateCreated };

    if (isNew) {
      const response = await createOrder(payload);
      setOrders(response);
    } else {
      const response = await updateOrder(id, payload);
      setOrders(response);
    }
  };

  const deleteRow = async (id, isLocal) => {
    if (isLocal) {
      setOrders(orders.filter(order => order.id !== id));
    } else {
      const response = await deleteOrder(id);
      setOrders(response);
    }
  };

  const handleSortChange = (id, key, isSorted, sortKey, componentValue) => {
    const newCriteria = isSorted
      ? [...sortCriteria, { id, key, sortKey, componentValue }]
      : sortCriteria.filter(criterion => criterion.key !== key || criterion.id !== id);

    setSortCriteria(newCriteria);
    console.log('Current sort criteria:', newCriteria);
    
    const updatedOrders = orders.map(order => 
      order.id === id ? { ...order, [key]: isSorted } : order
    );
    setOrders(updatedOrders);
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order, index) => (
            <TableRowComponent
              key={order.id}
              id={order.id}
              data={{ ...order, isNew: order.orderNumber === "" }}
              onSave={saveRow}
              onDelete={(isLocal) => deleteRow(order.id, isLocal)}
              onSortChange={handleSortChange}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
