import React, { useState, useEffect } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import TableRowComponent from './tableRow';

const TrackingStatus = () => {
  const columns = [
    "Номер заказа",
    "Сумма документа",
    "Номер телефона",
    "Полное имя",
    "Себестоимость",
    "Статус",
    "Дата отправки"
  ];

  const [orders, setOrders] = useState([]);

  useEffect(() => {
    setOrders([]);
  }, []);

  const addRow = () => {
    setOrders([...orders, { id: Date.now(), data: Array(7).fill('') }]);
  };

  const saveRow = (data, rowIndex) => {
    const newOrders = [...orders];
    newOrders[rowIndex].data = data;
    setOrders(newOrders);
  };

  const deleteRow = (rowIndex) => {
    const newOrders = orders.filter((_, index) => index !== rowIndex);
    setOrders(newOrders);
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order, index) => (
            <TableRowComponent
              key={order.id}
              onSave={(data) => saveRow(data, index)}
              onDelete={() => deleteRow(index)}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
