import React, { useState, useEffect, useRef } from 'react';
import { nanoid } from 'nanoid';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import TableRowComponent from './TableRowComponent';
import { createOrder } from '../../utils/backendApi'; // Импортируем функцию createOrder

const TrackingStatus = () => {
  const columns = [
    "Номер заказа",
    "Сумма документа",
    "Номер телефона",
    "Полное имя",
    "Себестоимость",
    "Статус",
    "Дата отправки"
  ];

  const initialOrder = {
    orderNumber: "",
    documentAmount: "",
    phoneNumber: "",
    fullName: "",
    costPrice: "",
    status: "",
    shippingDate: "",
    isSorted: false
  };

  const [orders, setOrders] = useState([{
    id: nanoid(),
    data: initialOrder
  }]);

  const isInitialMount = useRef(true);

  useEffect(() => {
    if (isInitialMount.current) {
      isInitialMount.current = false;
    }
  }, [orders]);

  const addRow = () => {
    setOrders([...orders, { id: nanoid(), data: initialOrder }]);
  };

  const saveRow = async (data, rowIndex) => {
    const { isSorted, ...orderData } = data; // Исключаем isSorted из данных для отправки на сервер
    const newOrders = [...orders];
    newOrders[rowIndex].data = data;
    setOrders(newOrders);

    try {
      const response = await createOrder(orderData);
      console.log('Order created:', response); // Логгирование успешного создания заказа
    } catch (error) {
      console.error('Error creating order:', error);
    }
  };

  const deleteRow = (rowIndex) => {
    const newOrders = orders.filter((_, index) => index !== rowIndex);
    setOrders(newOrders);
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order, index) => (
            <TableRowComponent
              key={order.id}
              data={order.data}
              onSave={(data) => saveRow(data, index)}
              onDelete={() => deleteRow(index)}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
