import React, { useState, useEffect } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { nanoid } from 'nanoid';
import TableRowComponent from './TableRowComponent';
import { fetchOrders, createOrder, updateOrder, deleteOrder } from '../../utils/backendApi';

const TrackingStatus = () => {
  const columns = [
    "Номер заказа",
    "Статус",
    "Дата отправки",
    "Номер телефона"
  ];

  const initialOrder = {
    id: nanoid(),
    orderNumber: "",
    phoneNumber: "",
    status: "",
    dateCreated: "",
    isNew: true
  };

  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const fetchedOrders = await fetchOrders();
      setOrders(fetchedOrders);
    };

    fetchData();
  }, []);

  const addRow = () => {
    setOrders([...orders, { ...initialOrder }]);
  };

  const saveRow = async (data) => {
    const { id, orderNumber, phoneNumber, status, dateCreated, isNew } = data;
    const payload = { id, orderNumber, phoneNumber, status, dateCreated };

    const isDuplicate = orders.some(order => order.orderNumber === orderNumber && order.id !== id);
    if (isDuplicate) {
      alert('Номер заказа уже существует');
      return;
    }

    if (isNew) {
      const response = await createOrder(payload);
      setOrders(response);
    } else {
      const response = await updateOrder(id, payload);
      setOrders(response);
    }
  };

  const deleteRow = async (id, isLocal) => {
    if (isLocal) {
      setOrders(orders.filter(order => order.id !== id));
    } else {
      const response = await deleteOrder(id);
      setOrders(response);
    }
  };

  const handleSortChange = (id, key, isSorted, componentName, componentValue) => {
    console.log(`Sort marker: ${isSorted}, Field: ${componentName}, Value: ${componentValue}`);
    const updatedOrders = orders.map(order => 
      order.id === id ? { ...order, [key]: isSorted } : order
    );
    setOrders(updatedOrders);
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order, index) => (
            <TableRowComponent
              key={order.id}
              id={order.id}
              data={{ ...order, isNew: order.orderNumber === "" }}
              orders={orders} // Передаем массив заказов
              onSave={saveRow}
              onDelete={(isLocal) => deleteRow(order.id, isLocal)}
              onSortChange={handleSortChange}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
