import React, { useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, Button } from '@mui/material';

const TrackingStatus = () => {
  const initialOrder = {
    id: 1,
    Number: '20400048699000',
    RecipientDateTime: '2024-05-24 12:00:00',
    Status: 'CargoDelivered',
    DocumentCost: '500',
    PhoneRecipient: '+380123456789',
    RecipientFullName: 'John Doe',
    DocumentWeight: '0.1',
  };

  const [orders, setOrders] = useState([initialOrder]);
  const [nextId, setNextId] = useState(2);
  const [errors, setErrors] = useState({});

  const handleNumberChange = (id, value) => {
    const updatedOrders = orders.map((order) =>
      order.id === id ? { ...order, Number: value } : order
    );

    const error = value.length !== 14 && value.length > 0 ? 'Введите 14-значный номер' : '';

    setErrors({ ...errors, [id]: error });
    setOrders(updatedOrders);
  };

  const addOrder = () => {
    setOrders([
      ...orders,
      { ...initialOrder, id: nextId, Number: '' },
    ]);
    setNextId(nextId + 1);
  };

  const removeOrder = (id) => {
    setOrders(orders.filter((order) => order.id !== id));
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Номер ЕН</TableCell>
            <TableCell>Дата отправки</TableCell>
            <TableCell>Статус отправки</TableCell>
            <TableCell>Сумма</TableCell>
            <TableCell>Номер телефона</TableCell>
            <TableCell>Имя</TableCell>
            <TableCell>Себестоимость</TableCell>
            <TableCell>Действия</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order) => (
            <TableRow key={order.id}>
              <TableCell>
                <TextField
                  value={order.Number}
                  onChange={(e) => handleNumberChange(order.id, e.target.value)}
                  placeholder={initialOrder.Number}
                  error={!!errors[order.id]}
                  helperText={errors[order.id]}
                />
              </TableCell>
              <TableCell>{order.RecipientDateTime}</TableCell>
              <TableCell>{order.Status}</TableCell>
              <TableCell>{order.DocumentCost}</TableCell>
              <TableCell>{order.PhoneRecipient}</TableCell>
              <TableCell>{order.RecipientFullName}</TableCell>
              <TableCell>{order.DocumentWeight}</TableCell>
              <TableCell>
                <Button onClick={() => addOrder()} variant="contained" color="primary">
                  Добавить
                </Button>
                <Button onClick={() => removeOrder(order.id)} variant="contained" color="secondary">
                  Удалить
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
