import React from 'react';
import { TextField } from '@mui/material';
import { FormControlLabel, Checkbox } from '@mui/material';

const CostInput = ({ value, onChange, showCostError, errorMessage, isSorted, onSortChange  }) => {
  const handleChange = (e) => {
    const newValue = e.target.value;
    onChange(newValue);
  };
  const handleCheckboxChange = (event) => {
    const isChecked = event.target.checked;
    onSortChange(isChecked, value);
  };

  return (
    <div>
      <TextField
        value={value}
        onChange={handleChange}
        variant="outlined"
        size="small"
        fullWidth
        placeholder="Сумма заказа"
        error={showCostError}
        helperText={showCostError ? errorMessage : ''}
        inputProps={{ maxLength: 14 }} // Ограничение на количество символов
        style={{ width: '170px' }} // Увеличиваем ширину на 10%
      />
      <FormControlLabel
        control={
          <Checkbox
            checked={isSorted ?? false}
            onChange={handleCheckboxChange}
          />
        }
        label="Фильтр"
      />
    </div>
  );
};

export default CostInput;
