import React, { useState, useEffect } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import TableRowComponent from './tableRowComponent';
import handleApiRequest from '../../utils/apiHandler';

const TrackingStatus = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    handleApiRequest('GET').then(setOrders).catch(console.error);
  }, []);

  const handleChange = (field, value, id) => {
    const updatedOrders = orders.map(order => {
      if (order.id === id) {
        return { ...order, [field]: value };
      }
      return order;
    });
    setOrders(updatedOrders);
  };

  const handleDelete = (id) => {
    handleApiRequest('DELETE', {}, id)
      .then(() => setOrders(orders.filter(order => order.id !== id)))
      .catch(console.error);
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {/* Headers here */}
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order, index) => (
            <TableRowComponent key={index} row={order} onChange={handleChange} onDelete={handleDelete} />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
