// src/components/ExpensesTable.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, TextField, Tooltip } from '@mui/material';

const initialRow = {
  id: null,
  date: '',
  advertising: '',
  returnCosts: '',
  defectCosts: '',
  hostingCosts: '',
  otherCosts: '',
  isSaved: false,
  dateError: false,
  inputError: false,
};

const ExpensesTable = () => {
  const [rows, setRows] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:4000/expenses');
      setRows(response.data.map(expense => ({ ...expense, isSaved: true })));
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const addRow = () => {
    const newRow = { ...initialRow, id: Date.now() };
    setRows([...rows, newRow]);
  };

  const handleInputChange = (id, field, value) => {
    const updatedRows = rows.map((row) => {
      if (row.id === id) {
        const dateError = field === 'date' && !value;
        const inputError = (field !== 'date' && field !== 'isSaved') && (isNaN(value) || value < 0);
        return { ...row, [field]: value, dateError, inputError };
      }
      return row;
    });
    setRows(updatedRows);
  };

  const handleNumericInputChange = (id, field, value) => {
    const numericValue = value.replace(/[^0-9]/g, '');
    handleInputChange(id, field, numericValue);
  };

  const removeRow = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/expenses/${id}`);
      setRows(rows.filter((row) => row.id !== id));
    } catch (error) {
      console.error('Error deleting expense:', error);
    }
  };

  const handleSaveOrUpdate = async (id) => {
    const rowToSave = rows.find(row => row.id === id);
    console.log('Row to Save or Update:', rowToSave);

    if (rowToSave.dateError || rowToSave.inputError) {
      console.error('Cannot save or update row with errors.');
      return;
    }

    if (!rowToSave.isSaved) {
      try {
        const response = await axios.post('http://localhost:4000/expenses', rowToSave);
        console.log('Expense saved:', response.data);
        setRows(rows.map(row => row.id === id ? { ...row, isSaved: true } : row));
        await fetchData();
      } catch (error) {
        console.error('Error saving expense:', error);
      }
    } else {
      try {
        await axios.patch(`http://localhost:4000/expenses/${id}`, rowToSave);
        console.log('Expense updated');
        await fetchData();
      } catch (error) {
        console.error('Error updating expense:', error);
      }
    }
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Дата</TableCell>
            <TableCell>Реклама</TableCell>
            <TableCell>Расходы на возврат</TableCell>
            <TableCell>Расходы на брак</TableCell>
            <TableCell>Расходы на хостинг</TableCell>
            <TableCell>Прочие расходы</TableCell>
            <TableCell>
              <Button variant="contained" color="primary" onClick={addRow}>
                Добавить
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.id}>
              <TableCell>
                <Tooltip title={row.dateError ? 'Введите дату' : ''} placement="top" arrow>
                  <TextField
                    type="date"
                    value={row.date}
                    onChange={(e) => handleInputChange(row.id, 'date', e.target.value)}
                    error={row.dateError}
                    helperText={row.dateError ? 'Введите дату' : ''}
                  />
                </Tooltip>
              </TableCell>
              <TableCell>
                <Tooltip title={row.inputError ? 'Введите корректную сумму' : ''} placement="top" arrow>
                  <TextField
                    value={row.advertising}
                    onChange={(e) => handleNumericInputChange(row.id, 'advertising', e.target.value)}
                    error={row.inputError}
                    helperText={row.inputError ? 'Введите корректную сумму' : ''}
                  />
                </Tooltip>
              </TableCell>
              <TableCell>
                <Tooltip title={row.inputError ? 'Введите корректную сумму' : ''} placement="top" arrow>
                  <TextField
                    value={row.returnCosts}
                    onChange={(e) => handleNumericInputChange(row.id, 'returnCosts', e.target.value)}
                    error={row.inputError}
                    helperText={row.inputError ? 'Введите корректную сумму' : ''}
                  />
                </Tooltip>
              </TableCell>
              <TableCell>
                <Tooltip title={row.inputError ? 'Введите корректную сумму' : ''} placement="top" arrow>
                  <TextField
                    value={row.defectCosts}
                    onChange={(e) => handleNumericInputChange(row.id, 'defectCosts', e.target.value)}
                    error={row.inputError}
                    helperText={row.inputError ? 'Введите корректную сумму' : ''}
                  />
                </Tooltip>
              </TableCell>
              <TableCell>
                <Tooltip title={row.inputError ? 'Введите корректную сумму' : ''} placement="top" arrow>
                  <TextField
                    value={row.hostingCosts}
                    onChange={(e) => handleNumericInputChange(row.id, 'hostingCosts', e.target.value)}
                    error={row.inputError}
                    helperText={row.inputError ? 'Введите корректную сумму' : ''}
                  />
                </Tooltip>
              </TableCell>
              <TableCell>
                <Tooltip title={row.inputError ? 'Введите корректную сумму' : ''} placement="top" arrow>
                  <TextField
                    value={row.otherCosts}
                    onChange={(e) => handleNumericInputChange(row.id, 'otherCosts', e.target.value)}
                    error={row.inputError}
                    helperText={row.inputError ? 'Введите корректную сумму' : ''}
                  />
                </Tooltip>
              </TableCell>
              <TableCell>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleSaveOrUpdate(row.id)}
                  disabled={row.dateError || row.inputError}
                >
                  {row.isSaved ? 'Обновить' : 'Записать'}
                </Button>
              </TableCell>
              <TableCell>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => removeRow(row.id)}
                >
                  Удалить
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default ExpensesTable;
