import React, { useState, useEffect } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { nanoid } from 'nanoid';
import TableRowComponent from './TableRowComponent';
import { fetchOrders, createOrder, updateOrder, deleteOrder } from '../../utils/backendApi';

const TrackingStatus = () => {
  const columns = ['Номер заказа', 'Статус', 'Дата отправки'];

  const initialOrder = {
    id: nanoid(),
    orderNumber: '',
    status: '',
    dateCreated: '',
    isNew: true
  };

  const [orders, setOrders] = useState([]);
  const [sortField, setSortField] = useState('');
  const [isSorted, setIsSorted] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const fetchedOrders = await fetchOrders();
      setOrders(fetchedOrders);
    };

    fetchData();
  }, []);

  const existingOrderNumbers = orders.map((order) => order.orderNumber);

  const addRow = () => {
    setOrders([...orders, { ...initialOrder, isNew: true }]);
  };

  const saveRow = async (data) => {
    const { id, orderNumber, status, dateCreated, isNew } = data;
    const payload = { id, orderNumber, status, dateCreated, isNew };

    if (isNew) {
      const response = await createOrder(payload);
      setOrders(response);
    } else {
      const response = await updateOrder(id, payload);
      setOrders(response);
    }
  };

  const deleteRow = async (id, isLocal) => {
    if (isLocal) {
      setOrders(orders.filter((order) => order.id !== id));
    } else {
      const response = await deleteOrder(id);
      setOrders(response);
    }
  };

  const handleSortChange = (id, key, isSorted) => {
    if (isSorted) {
      setSortField(key);
    } else {
      setSortField('');
    }
    setIsSorted(isSorted);
  };

  const getSortedOrders = () => {
    if (!isSorted || !sortField) return orders;

    return [...orders].sort((a, b) => {
      if (a[sortField] < b[sortField]) return -1;
      if (a[sortField] > b[sortField]) return 1;
      return 0;
    });
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell>
              <Button color='primary' variant='contained' onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {getSortedOrders().map((order) => (
            <TableRowComponent
              key={order.id}
              id={order.id}
              data={{ ...order, isNew: order.isNew }}
              existingOrderNumbers={existingOrderNumbers}
              onSave={saveRow}
              onDelete={(isLocal) => deleteRow(order.id, isLocal)}
              onSortChange={handleSortChange} // Добавляем обработчик изменений чекбокса
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
