// src/components/ExpensesTable.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, TextField } from '@mui/material';

const initialRow = {
  id: null,
  date: '',
  advertising: '',
  returnCosts: '',
  defectCosts: '',
  hostingCosts: '',
  otherCosts: '',
  isSaved: false,
};

const ExpensesTable = () => {
  const [rows, setRows] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:4000/expenses');
        setRows(response.data.map(expense => ({ ...expense, isSaved: true })));
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const addRow = () => {
    const newRow = { ...initialRow, id: Date.now() };
    setRows([...rows, newRow]);
  };

  const handleInputChange = (id, field, value) => {
    const updatedRows = rows.map((row) =>
      row.id === id ? { ...row, [field]: value } : row
    );
    setRows(updatedRows);
  };

  const removeRow = (id) => {
    setRows(rows.filter((row) => row.id !== id));
  };

  const handleSaveOrUpdate = async (id) => {
    const rowToSave = rows.find(row => row.id === id);
    console.log('Row to Save or Update:', rowToSave);

    if (!rowToSave.isSaved) {
      try {
        const response = await axios.post('http://localhost:4000/expenses', rowToSave);
        console.log('Expense saved:', response.data);
        setRows(rows.map(row => row.id === id ? { ...row, isSaved: true } : row));
      } catch (error) {
        console.error('Error saving expense:', error);
      }
    } else {
      try {
        await axios.patch(`http://localhost:4000/expenses/${id}`, rowToSave);
        console.log('Expense updated');
      } catch (error) {
        console.error('Error updating expense:', error);
      }
    }

    console.log('Updated Rows: ', rows);
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Дата</TableCell>
            <TableCell>Реклама</TableCell>
            <TableCell>Расходы на возврат</TableCell>
            <TableCell>Расходы на брак</TableCell>
            <TableCell>Расходы на хостинг</TableCell>
            <TableCell>Прочие расходы</TableCell>
            <TableCell>
              <Button variant="contained" color="primary" onClick={addRow}>
                Добавить
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.id}>
              <TableCell>
                <TextField
                  type="date"
                  value={row.date}
                  onChange={(e) => handleInputChange(row.id, 'date', e.target.value)}
                />
              </TableCell>
              <TableCell>
                <TextField
                  value={row.advertising}
                  onChange={(e) => handleInputChange(row.id, 'advertising', e.target.value)}
                />
              </TableCell>
              <TableCell>
                <TextField
                  value={row.returnCosts}
                  onChange={(e) => handleInputChange(row.id, 'returnCosts', e.target.value)}
                />
              </TableCell>
              <TableCell>
                <TextField
                  value={row.defectCosts}
                  onChange={(e) => handleInputChange(row.id, 'defectCosts', e.target.value)}
                />
              </TableCell>
              <TableCell>
                <TextField
                  value={row.hostingCosts}
                  onChange={(e) => handleInputChange(row.id, 'hostingCosts', e.target.value)}
                />
              </TableCell>
              <TableCell>
                <TextField
                  value={row.otherCosts}
                  onChange={(e) => handleInputChange(row.id, 'otherCosts', e.target.value)}
                />
              </TableCell>
              <TableCell>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleSaveOrUpdate(row.id)}
                >
                  {row.isSaved ? 'Обновить' : 'Записать'}
                </Button>
              </TableCell>
              <TableCell>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => removeRow(row.id)}
                >
                  Удалить
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default ExpensesTable;
