import React from 'react';
import { TextField, FormControlLabel, Checkbox } from '@mui/material';
import InputMask from 'react-input-mask';

const PhoneNumberInput = ({ value, onChange, isSorted, onSortChange, showError, errorMessage }) => {
  const handleChange = (e) => {
    const newValue = e.target.value;
    onChange(newValue);
  };

  return (
    <div>
      <InputMask
        mask="(999) 999-9999"
        value={value}
        onChange={handleChange}
        onBlur={handleChange}
      >
        {() => (
          <TextField
            value={value}
            onChange={handleChange}
            variant="outlined"
            size="small"
            fullWidth
            placeholder="Номер телефона"
            error={showError}
            helperText={showError ? errorMessage : ''}
          />
        )}
      </InputMask>
      <FormControlLabel
        control={<Checkbox />}
        label="Сортировка"
      />
    </div>
  );
};

export default PhoneNumberInput;
