import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { nanoid } from 'nanoid';
import TableRowComponent from './TableRowComponent';
import { fetchApi } from '../../utils/backendApi';
import { useDispatch, useSelector } from 'react-redux';
import { addStartDate, addEndDate } from '../../store/actions';

const TrackingStatus = () => {
  const dispatch = useDispatch();
  const columns = ["Номер заказа", "Статус", "Дата отправки", "Сумма документа", "Номер телефона", "Себестоимость"];
  const initialOrder = {
    id: nanoid(),
    orderNumber: "",
    status: "",
    date: "",
    documentSum: "",
    phoneNumber: "", 
    cost: "",
    isNew: true,
    checkedStatuses: {
      isPhoneNumberChecked: false
    }
  };

  const fetchOrdersUrl = "http://localhost:4000/orders";

  const [orders, setOrders] = useState([]);
  const [filterPhoneNumber, setFilterPhoneNumber] = useState(""); // Состояние для фильтрации по номеру телефона
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const reduxStartDate = useSelector(state => state.startDate);
  const reduxEndDate = useSelector(state => state.endDate);
  const previousStartDate= useRef("");
  const previousEndDate= useRef("");


  useEffect(() => {
    const fetchData = async () => {
      const fetchedOrders = await fetchApi(fetchOrdersUrl, 'GET');
      setOrders(fetchedOrders.map(order => ({
        ...order,
        checkedStatuses: order.checkedStatuses || { isPhoneNumberChecked: false }
      })));
    };

    fetchData();
  }, []);

  const existingOrderNumbers = useMemo(() => orders.map(order => order.orderNumber), [orders]);

  const addRow = () => {
    setOrders([...orders, { ...initialOrder, isNew: true, checkedStatuses: { isPhoneNumberChecked: false } }]);
  };

  const saveRow = useCallback(async (data) => {
    const { id, orderNumber, status, date, documentSum, phoneNumber, cost, isNew } = data;
    const payload = { id, orderNumber, status, date, documentSum, phoneNumber, cost };

    if (isNew) {
      const response = await fetchApi(fetchOrdersUrl, 'POST', null, payload);
      setOrders(response);
    } else {
      const response = await fetchApi(fetchOrdersUrl, 'PATCH', id, payload);
      setOrders(response);
    }
  }, [setOrders]);

  const deleteRow = useCallback(async (id, isNew) => {
    if (isNew) {
      setOrders(orders => orders.filter(order => order.id !== id));
    } else {
      const response = await fetchApi(fetchOrdersUrl, 'DELETE', id);
      setOrders(response);
    }
  }, [setOrders]);

  const handleSortChange = (sortKey, isSorted, filterValue, id) => {
    setOrders(orders.map(order => 
      order.id === id ? { ...order, checkedStatuses: { ...order.checkedStatuses, [sortKey]: isSorted } } : order
    ));

    if (sortKey === "isPhoneNumberChecked" && isSorted){
      setFilterPhoneNumber(filterValue);
    } else {
      setFilterPhoneNumber("");
    }
  };

  const filteredOrders = orders.filter(order => {
    const orderDate = new Date(order.date);
    const isWithinDateRange =
    (!startDate || orderDate >= new Date(startDate)) &&
    (!endDate || orderDate <= new Date(endDate));   

    return (
      isWithinDateRange &&
      (!filterPhoneNumber || order.phoneNumber === filterPhoneNumber)
    );
  });
  
  useEffect(() => {
    if (JSON.stringify(startDate) !== JSON.stringify(previousStartDate.current)) {
      previousStartDate.current = startDate;
      dispatch(addStartDate(startDate));
    }
    if (JSON.stringify(endDate) !== JSON.stringify(previousEndDate.current)) {
      previousEndDate.current = endDate;
      dispatch(addEndDate(endDate));
    }
  }, [startDate, endDate, dispatch]);

  useEffect(() => {
    console.log('StartDate:', reduxStartDate);
    console.log('EndDate:', reduxEndDate);
  }, [reduxStartDate, reduxEndDate]);
  

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}{column === "Дата отправки" ? (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                  <input
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                    style={{ display: "block", marginTop: "8px" }}
                  />
                  <input
                    type="date"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                    style={{ display: "block", marginTop: "8px" }}
                  />
                </div>
              ) : (
                ""
              )}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {filteredOrders.map((order) => (
            <TableRowComponent
              key={order.id}
              id={order.id}
              data={{ ...order, isNew: order.isNew }}
              existingOrderNumbers={existingOrderNumbers}
              onSave={saveRow}
              onDelete={(isNew) => deleteRow(order.id, isNew)}
              onSortChange={handleSortChange}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
