import React from 'react';
import { TextField, FormControlLabel, Checkbox } from '@mui/material';

const PhoneNumberInput = ({ value, onChange, showError, errorMessage, isSorted, onSortChange }) => {
  const handleChange = (e) => {
    onChange(e.target.value);
  };

  const handleSortChange = (e) => {
    const isChecked = e.target.checked;
    onSortChange('phoneNumber', isChecked, value);
  };

  return (
    <div>
      <TextField
        value={value}
        onChange={handleChange}
        variant="outlined"
        size="small"
        fullWidth
        placeholder="Номер телефона"
        error={showError}
        helperText={showError ? errorMessage : ''}
      />
      <FormControlLabel
        control={<Checkbox checked={Boolean(isSorted)} onChange={handleSortChange} />}
        label="Сортировка"
      />
    </div>
  );
};

export default PhoneNumberInput;
