import React, { useState, useEffect, useRef } from 'react';
import { nanoid } from 'nanoid';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import TableRowComponent from './TableRowComponent';

const TrackingStatus = () => {
  const columns = [
    "Order Number",
    "Document Amount",
    "Phone Number",
    "Full Name",
    "Cost Price",
    "Status",
    "Shipping Date"
  ];

  const initialOrder = {
    orderNumber: "",
    documentAmount: "",
    phoneNumber: "",
    fullName: "",
    costPrice: "",
    status: "",
    shippingDate: ""
  };

  const [orders, setOrders] = useState([{
    id: nanoid(),
    data: initialOrder
  }]);

  const isInitialMount = useRef(true);

  useEffect(() => {
    if (isInitialMount.current) {
      console.log('Order data array:', orders);
      isInitialMount.current = false;
    }
  }, [orders]);

  const addRow = () => {
    setOrders([...orders, { id: nanoid(), data: initialOrder }]);
  };

  const saveRow = (data, isSorted, rowIndex) => {
    const newOrders = [...orders];
    newOrders[rowIndex].data = data;
    setOrders(newOrders);

    // Output order number and sorting status to the console
    console.log(`Order number (row ${rowIndex + 1}): ${data.orderNumber}`);
    console.log(`Sorting status (row ${rowIndex + 1}): ${isSorted}`);
  };

  const deleteRow = (rowIndex) => {
    const newOrders = orders.filter((_, index) => index !== rowIndex);
    setOrders(newOrders);
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Add Row
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order, index) => (
            <TableRowComponent
              key={order.id}
              data={order.data}
              onSave={(data, isSorted) => saveRow(data, isSorted, index)}
              onDelete={() => deleteRow(index)}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
