import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, Button, Box } from '@mui/material';
import InputMask from 'react-input-mask';

const TrackingStatus = () => {
  const initialOrder = {
    id: null,
    Number: '',
    DateCreated: '',
    Status: '',
    DocumentCost: '',
    PhoneRecipient: '',
    RecipientFullName: '',
    CostOfGoods: '',
  };

  const [orders, setOrders] = useState([initialOrder]);
  const [errors, setErrors] = useState({});
  const [phoneErrors, setPhoneErrors] = useState({});
  const [costErrors, setCostErrors] = useState({});
  const [nameErrors, setNameErrors] = useState({});

  const fetchOrderDetails = async (trackingNumber) => {
    try {
      const response = await axios.post('https://api.novaposhta.ua/v2.0/json/', {
        apiKey: 'a3e0146033929135960600cd27f26325',
        modelName: 'TrackingDocument',
        calledMethod: 'getStatusDocuments',
        methodProperties: {
          Documents: [
            {
              DocumentNumber: trackingNumber,
              Phone: '',
            },
          ],
        },
      });

      if (response.data.data && response.data.data.length > 0) {
        const data = response.data.data[0];
        return {
          DateCreated: data.DateCreated,
          Status: data.Status,
        };
      } else {
        console.error('No data found for the given tracking number');
        return {};
      }
    } catch (error) {
      console.error('Error fetching order details:', error);
      return {};
    }
  };

  const updateOrderDetails = useCallback(async (order) => {
    if (order.Number) {
      const data = await fetchOrderDetails(order.Number);
      return { ...order, ...data };
    }
    return order;
  }, []);

  const fetchAndUpdateOrders = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:4000/orders');
      console.log('Fetched Orders:', response.data);
      const ordersWithIds = response.data.map(order => ({ ...order }));
      const updatedOrders = await Promise.all(
        ordersWithIds.map(order => updateOrderDetails(order))
      );
      setOrders(updatedOrders);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  }, [updateOrderDetails]);

  useEffect(() => {
    fetchAndUpdateOrders();
  }, [fetchAndUpdateOrders]);

  const handleNumberChange = (id, value) => {
    const updatedOrders = orders.map((order) =>
      order.id === id ? { ...order, Number: value } : order
    );

    const error = value.length !== 14 && value.length > 0 ? 'Введите 14-значный номер' : '';

    setErrors({ ...errors, [id]: error });
    setOrders(updatedOrders);
  };

  const handlePhoneChange = (id, value) => {
    const updatedOrders = orders.map((order) =>
      order.id === id ? { ...order, PhoneRecipient: value } : order
    );

    const error = /^\+380\d{9}$/.test(value.replace(/\s/g, '')) ? '' : 'Введите корректный номер';

    setPhoneErrors({ ...phoneErrors, [id]: error });
    setOrders(updatedOrders);
  };

  const handleCostChange = (id, value) => {
    const updatedOrders = orders.map((order) =>
      order.id === id ? { ...order, DocumentCost: value } : order
    );

    const error = /^\d+$/.test(value) ? '' : 'Введите корректную сумму';

    setCostErrors({ ...costErrors, [id]: error });
    setOrders(updatedOrders);
  };

  const handleNameChange = (id, value) => {
    const updatedOrders = orders.map((order) =>
      order.id === id ? { ...order, RecipientFullName: value } : order
    );

    const error = /^[a-zA-Zа-яА-ЯёЁ\s]+$/.test(value) ? '' : 'Имя не должно содержать цифры';

    setNameErrors({ ...nameErrors, [id]: error });
    setOrders(updatedOrders);
  };

  const handleInputChange = (id, field, value) => {
    const updatedOrders = orders.map((order) =>
      order.id === id ? { ...order, [field]: value } : order
    );
    setOrders(updatedOrders);
  };

  const generateRandomId = () => {
    return Math.floor(1000 + Math.random() * 9000);
  };

  const handleSaveOrUpdate = async (id) => {
    const order = orders.find(order => order.id === id);
    console.log('Updated Order:', order);

    if (order.id === null) {
      const newOrderId = generateRandomId();
      const updatedOrder = { ...order, id: newOrderId };
      try {
        await axios.post('http://localhost:4000/orders', updatedOrder);
        console.log('Order saved');
        const data = await fetchOrderDetails(order.Number);
        setOrders(orders.map(o => o.id === id ? { ...updatedOrder, ...data } : o));
      } catch (error) {
        console.error('Error saving order:', error);
      }
    } else {
      try {
        await axios.patch(`http://localhost:4000/orders/${id}`, order);
        console.log('Order updated');
        const data = await fetchOrderDetails(order.Number);
        setOrders(orders.map(o => o.id === id ? { ...o, ...data } : o));
      } catch (error) {
        console.error('Error updating order:', error);
      }
    }
  };

  const addOrder = () => {
    setOrders([
      ...orders,
      { ...initialOrder },
    ]);
  };

  const removeOrder = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/orders/${id}`);
      setOrders(orders.filter((order) => order.id !== id));
      setErrors(Object.fromEntries(Object.entries(errors).filter(([key]) => key !== String(id))));
    } catch (error) {
      console.error('Error deleting order:', error);
    }
  };

  return (
    <>
      <TableContainer component={Paper} style={{ marginBottom: '150px' }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Номер ЕН</TableCell>
              <TableCell>Сумма</TableCell>
              <TableCell>Номер телефона</TableCell>
              <TableCell>Имя</TableCell>
              <TableCell>Себестоимость</TableCell>
              <TableCell>Дата отправки</TableCell>
              <TableCell>Статус отправки</TableCell>
              <TableCell>
                <Button onClick={addOrder} variant="contained" color="primary" style={{ width: '100px' }}>
                  Добавить
                </Button>
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map((order) => (
              <TableRow key={order.id || 'new'}>
                <TableCell>
                  <TextField
                    value={order.Number}
                    onChange={(e) => handleNumberChange(order.id, e.target.value)}
                    placeholder="Введите номер"
                    error={!!errors[order.id]}
                    helperText={errors[order.id]}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={order.DocumentCost}
                    onChange={(e) => handleCostChange(order.id, e.target.value)}
                    placeholder="Введите сумму"
                    error={!!costErrors[order.id]}
                    helperText={costErrors[order.id]}
                  />
                </TableCell>
                <TableCell>
                  <InputMask
                    mask="+380 99 999 99 99"
                    maskChar="_"
                    value={order.PhoneRecipient}
                    onChange={(e) => handlePhoneChange(order.id, e.target.value)}
                  >
                    {(inputProps) => (
                      <TextField
                        {...inputProps}
                        placeholder="Введите номер телефона"
                        error={!!phoneErrors[order.id]}
                        helperText={phoneErrors[order.id]}
                      />
                    )}
                  </InputMask>
                </TableCell>
                <TableCell>
                  <TextField
                    value={order.RecipientFullName}
                    onChange={(e) => handleNameChange(order.id, e.target.value)}
                    placeholder="Введите имя"
                    error={!!nameErrors[order.id]}
                    helperText={nameErrors[order.id]}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={order.CostOfGoods}
                    onChange={(e) => handleInputChange(order.id, 'CostOfGoods', e.target.value)}
                    placeholder="Введите себестоимость"
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={order.DateCreated || ''}
                    placeholder="-"
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    value={order.Status || ''}
                    placeholder="-"
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </TableCell>
                <TableCell>
                  <Button
                    onClick={() => handleSaveOrUpdate(order.id)}
                    variant="contained"
                    color="primary"
                    style={{ width: '100px' }}
                  >
                    {order.id ? 'Обновить' : 'Записать'}
                  </Button>
                </TableCell>
                {order.id && (
                  <TableCell>
                    <Button onClick={() => removeOrder(order.id)} variant="contained" color="secondary" style={{ width: '100px' }}>
                      Удалить
                    </Button>
                  </TableCell>
                )}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Box position="fixed" bottom={0} left={0} right={0} bgcolor="background.paper" borderTop="1px solid #ddd">
        <TableContainer component={Paper} style={{ maxHeight: '100px' }}>
          <Table stickyHeader>
            <TableHead>
              <TableRow>
                <TableCell>1</TableCell>
                <TableCell>2</TableCell>
                <TableCell>3</TableCell>
                <TableCell>4</TableCell>
                <TableCell>5</TableCell>
                <TableCell>6</TableCell>
                <TableCell>7</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              <TableRow>
                <TableCell></TableCell>
                <TableCell></TableCell>
                <TableCell></TableCell>
                <TableCell></TableCell>
                <TableCell></TableCell>
                <TableCell></TableCell>
                <TableCell></TableCell>
              </TableRow>
            </TableBody>
          </Table>
        </TableContainer>
      </Box>
    </>
  );
};

export default TrackingStatus;
