import React from 'react';
import { TextField } from '@mui/material';

const DocumentAmountInput = ({ value, onChange, showError, errorMessage }) => {
  const handleChange = (e) => {
    const newValue = e.target.value;
    if (/^\d*$/.test(newValue)) {
      onChange(newValue);
    }
  };

  return (
    <TextField
      value={value}
      onChange={handleChange}
      variant="outlined"
      size="small"
      fullWidth
      placeholder="Сумма документа"
      error={showError}
      helperText={showError ? errorMessage : ''}
    />
  );
};

export default DocumentAmountInput;
