import React, { useState, useEffect, useCallback } from 'react';
import { TableRow, TableCell, Button } from '@mui/material';
import DocumentSumInput from './DocumentSumInput';
import DocumentCost from './DocumentCost';

const buttonStyle = {
  minWidth: '50px',
  padding: '4px 8px'
};

const ExpensesRowComponent = ({ id, data, onSave, onDelete }) => {

  const [inputs, setInputs] = useState({
    ...data,
    isStatusSorted: checkedStatuses.isStatusChecked,
    isDateSorted: checkedStatuses.isDateCreatedChecked,
    isDocumentSumChecked: checkedStatuses.isDocumentSumChecked,
    isPhoneNumberChecked: checkedStatuses.isPhoneNumberChecked,
    isDocumentCostChecked: checkedStatuses.isDocumentCostChecked
  });

  const [showSumError, setShowSumError] = useState(false);
  const [showCostError, setShowCostError] = useState(false);

  useEffect(() => {
    setInputs({
      ...data,
      isStatusSorted: checkedStatuses.isStatusChecked,
      isDateSorted: checkedStatuses.isDateCreatedChecked,
      isDocumentSumChecked: checkedStatuses.isDocumentSumChecked,
      isPhoneNumberChecked: checkedStatuses.isPhoneNumberChecked,
      isDocumentCostChecked: checkedStatuses.isDocumentCostChecked
    });
  }, [data, checkedStatuses.isStatusChecked, checkedStatuses.isDateCreatedChecked, checkedStatuses.isDocumentSumChecked, checkedStatuses.isPhoneNumberChecked, checkedStatuses.isDocumentCostChecked]);

  const handleInputChange = useCallback((key, value) => {
    const newInputs = { ...inputs, [key]: value, error: false};
    setInputs(newInputs);
    if (key === "documentSum") {
      setShowSumError(false);
    }else if (key === "documentCost") {
      setShowCostError(false);
    }
  }, [inputs]);

  const handleSave = () => {
    const { documentSum, documentCost } = inputs;
    

    if (!/^\d+$/.test(documentSum)) {
      setShowSumError(true);
      return;
    }

    if (!/^\d+$/.test(documentCost)) {
      setShowCostError(true);
      return;
    }

    const { id, status, dateCreated, isNew } = inputs;
    const payload = { id, orderNumber, status, dateCreated, documentSum, phoneNumber, documentCost, isNew };
    onSave(payload);

    setShowSumError(false);
    setShowCostError(false);
  };

  const handleDelete = () => {
    const isEmpty = Object.values(inputs).every(value => value === "" || value === false);
    if (isEmpty) {
      onDelete(true);
    } else {
      onDelete(false);
    }
  };

  return (
    <TableRow key={id}>
      <TableCell key={`documentSum-${id}`}>
        <DocumentSumInput
          value={inputs.documentSum}
          onChange={(value) => handleInputChange("documentSum", value)}
          showSumError={showSumError}
          errorMessage='Сумма должна быть целым положительным числом'
        />
      </TableCell>
      <TableCell key={`documentCost-${id}`}>
        <DocumentCost
          value={inputs.documentCost}
          onChange={(value) => handleInputChange("documentCost", value)}
          showSumError={showCostError}
          errorMessage='Сумма должна быть целым положительным числом'
        />
      </TableCell>
      <TableCell key={`saveButton-${id}`}>
        <Button
          color="primary"
          variant="contained"
          onClick={handleSave}
          disabled={!data.isNew && !inputs.isChanged}
          style={buttonStyle}
        >
          {data.isNew ? "Записать" : "Обновить"}
        </Button>
      </TableCell>
      <TableCell key={`deleteButton-${id}`}>
        <Button color="secondary" variant="contained" onClick={handleDelete} style={buttonStyle}>
          Удалить
        </Button>
      </TableCell>
    </TableRow>
  );
};

export default ExpensesRowComponent;
