import React, { useState, useEffect, memo, useCallback } from 'react';
import { TableRow, TableCell, Button } from '@mui/material';
import OrderNumberInput from './OrderNumberInput';
import OrderStatus from './OrderStatus';
import DateCreated from './DateCreated';

const buttonStyle = {
  minWidth: '50px',
  padding: '4px 8px'
};

const TableRowComponent = memo(({ id, data, onSave, onDelete, onSortChange, existingOrderNumbers }) => {
  const [inputs, setInputs] = useState({
    ...data,
    isSorted: data.isSorted || false,
    isStatusSorted: data.isStatusSorted || false,
    isDateSorted: data.isDateSorted || false
  });
  const [showError, setShowError] = useState(false);
  const [duplicateError, setDuplicateError] = useState(false);

  useEffect(() => {
    setInputs({
      ...data,
      isSorted: data.isSorted || false,
      isStatusSorted: data.isStatusSorted || false,
      isDateSorted: data.isDateSorted || false
    });
  }, [data]);

  const handleInputChange = useCallback((key, value) => {
    const newInputs = { ...inputs, [key]: value, error: false, isChanged: key !== "isStatusSorted" && key !== "isDateSorted" };
    setInputs(newInputs);
    if (key === "orderNumber") {
      setShowError(false);
      setDuplicateError(false);
    }
  }, [inputs]);

  const handleSortChange = useCallback((sortKey, isSorted, componentValue, renderKey) => {
    handleInputChange(sortKey, isSorted);
    onSortChange(id, sortKey, isSorted, componentValue, renderKey);
  }, [handleInputChange, onSortChange, id]);

  const handleSave = () => {
    const { orderNumber } = inputs;

    if (orderNumber.length !== 14 || !/^\d+$/.test(orderNumber)) {
      setShowError(true);
      return;
    }

    if (existingOrderNumbers.includes(orderNumber) && orderNumber !== data.orderNumber) {
      setDuplicateError(true);
      return;
    }

    const { id, status, dateCreated, isNew } = inputs;
    const payload = { id, orderNumber, status, dateCreated, isNew };
    onSave(payload);

    setShowError(false);
    setDuplicateError(false);
  };

  const handleDelete = () => {
    const isEmpty = Object.values(inputs).every(value => value === "" || value === false);
    if (isEmpty) {
      onDelete(true);
    } else {
      onDelete(false);
    }
  };

  return (
    <TableRow key={id}>
      <TableCell key={`orderNumber-${id}`}>
        <OrderNumberInput
          value={inputs.orderNumber}
          onChange={(value) => handleInputChange("orderNumber", value)}
          showError={showError || duplicateError}
          errorMessage={duplicateError ? 'Номер заказа уже существует' : 'Номер должен быть целым положительным 14-значным числом'}
        />
      </TableCell>
      <TableCell key={`status-${id}`}>
        <OrderStatus
          status={inputs.status}
          isSorted={Boolean(inputs.isStatusSorted)}
          onSortChange={(isSorted) => handleSortChange("isStatusSorted", isSorted, inputs.status, "status")}
        />
      </TableCell>
      <TableCell key={`dateCreated-${id}`}>
        <DateCreated
          date={inputs.dateCreated}
          isSorted={Boolean(inputs.isDateSorted)}
          onSortChange={(isSorted) => handleSortChange("isDateSorted", isSorted, inputs.dateCreated, "dateCreated")}
        />
      </TableCell>
      <TableCell key={`saveButton-${id}`}>
        <Button
          color="primary"
          variant="contained"
          onClick={handleSave}
          disabled={!data.isNew && !inputs.isChanged}
          style={buttonStyle}
        >
          {data.isNew ? "Записать" : "Обновить"}
        </Button>
      </TableCell>
      <TableCell key={`deleteButton-${id}`}>
        <Button color="secondary" variant="contained" onClick={handleDelete} style={buttonStyle}>
          Удалить
        </Button>
      </TableCell>
    </TableRow>
  );
});

export default TableRowComponent;
