import React, { useState } from 'react';
import { TableRow, TableCell, Button } from '@mui/material';
import OrderNumberInput from './OrderNumberInput';

const TableRowComponent = ({ data, onSave, onDelete }) => {
  const [inputs, setInputs] = useState(data);

  const handleInputChange = (index, value) => {
    const newInputs = [...inputs];
    newInputs[index] = value;
    setInputs(newInputs);
  };

  const handleSave = () => {
    onSave(inputs);
  };

  const handleDelete = () => {
    onDelete();
  };

  return (
    <TableRow>
      <TableCell>
        <OrderNumberInput
          value={inputs[0]}
          onChange={(value) => handleInputChange(0, value)}
        />
      </TableCell>
      {inputs.slice(1).map((field, index) => (
        <TableCell key={index + 1}>
          {/* Здесь позже будет рендеринг отдельных компонентов для каждого поля */}
          {field}
        </TableCell>
      ))}
      <TableCell>
        <Button color="primary" variant="contained" onClick={handleSave}>
          Записать
        </Button>
      </TableCell>
      <TableCell>
        <Button color="secondary" variant="contained" onClick={handleDelete}>
          Удалить
        </Button>
      </TableCell>
    </TableRow>
  );
};

export default TableRowComponent;
