import React, { useState, useEffect } from 'react';
import { nanoid } from 'nanoid';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import TableRowComponent from './TableRowComponent';
import { fetchOrders, createOrder, updateOrder } from '../../utils/backendApi';

const TrackingStatus = () => {
  const columns = [
    "Номер заказа",
    "Сумма документа",
    "Номер телефона",
    "Полное имя",
    "Себестоимость",
    "Статус",
    "Дата отправки"
  ];

  const initialOrder = {
    orderNumber: "",
    documentAmount: "",
    phoneNumber: "",
    fullName: "",
    costPrice: "",
    status: "",
    shippingDate: "",
    isSorted: false,
    isNew: true
  };

  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const getOrders = async () => {
      try {
        const fetchedOrders = await fetchOrders();
        const ordersWithIds = fetchedOrders.map(order => ({
          id: nanoid(),
          data: { ...order, isSorted: false, isNew: false } // Добавляем поля isSorted и isNew
        }));
        setOrders(ordersWithIds);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    getOrders();
  }, []);

  const addRow = () => {
    setOrders([...orders, { id: nanoid(), data: initialOrder }]);
  };

  const saveRow = async (data, rowIndex) => {
    if (data.orderNumber.length !== 14 || !/^\d+$/.test(data.orderNumber)) {
      setOrders((prevOrders) => {
        const newOrders = [...prevOrders];
        newOrders[rowIndex].data = { ...data, error: true };
        return newOrders;
      });
      return;
    }

    const { isSorted, isNew, error, ...orderData } = data;
    const newOrders = [...orders];
    newOrders[rowIndex].data = data;
    setOrders(newOrders);

    try {
      if (isNew) {
        const response = await createOrder(orderData);
        console.log('Order created:', response);
        newOrders[rowIndex].data.isNew = false; // Помечаем строку как не новую
        setOrders(newOrders);
      } else {
        const response = await updateOrder(data.id, orderData);
        console.log('Order updated:', response);
      }
    } catch (error) {
      console.error('Error saving order:', error);
    }
  };

  const deleteRow = (rowIndex) => {
    const newOrders = orders.filter((_, index) => index !== rowIndex);
    setOrders(newOrders);
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map((order, index) => (
            <TableRowComponent
              key={order.id}
              data={order.data}
              onSave={(data) => saveRow(data, index)}
              onDelete={() => deleteRow(index)}
              isNew={order.data.isNew}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
