import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import InputMask from 'react-input-mask';
import AdapterDateFns from '@date-io/date-fns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DatePicker from '@mui/lab/DatePicker';

const InputField = ({ value, onChange, marker }) => {
  const [error, setError] = useState(false);
  const [helperText, setHelperText] = useState('');

  const handleBlur = () => {
    let errorText = '';
    setError(false);

    switch (marker) {
      case 'text':
        if (!/^[a-zA-Z\s]*$/.test(value)) {
          errorText = 'Введите корректный текст';
        }
        break;
      case 'phone':
        if (!/^\+?[1-9][0-9]{10,14}$/.test(value)) {
          errorText = 'Введите корректный номер телефона';
        }
        break;
      case 'money_amount':
        if (!/^\d+$/.test(value)) {
          errorText = 'Введите корректную сумму';
        }
        break;
      case 'delivery_number':
        if (!/^\d{14}$/.test(value)) {
          errorText = 'Введите корректный 14-значный номер';
        }
        break;
      case 'delivery_date':
        // Дополнительная валидация даты может быть добавлена здесь, если нужно.
        break;
      default:
        errorText = '';
    }

    if (errorText) {
      setError(true);
      setHelperText(errorText);
    } else {
      setError(false);
      setHelperText('');
    }
  };

  const handleChange = (newValue) => {
    onChange(newValue);
  };

  if (marker === 'delivery_date') {
    return (
      <LocalizationProvider dateAdapter={AdapterDateFns}>
        <DatePicker
          label="Delivery Date"
          value={value}
          onChange={handleChange}
          renderInput={(params) => (
            <TextField
              {...params}
              fullWidth
              error={error}
              helperText={helperText || params.helperText}
              onBlur={handleBlur}
            />
          )}
        />
      </LocalizationProvider>
    );
  }

  const getInputProps = () => {
    if (marker === 'phone') {
      return {
        mask: '+\\1 999 999 9999',
        maskPlaceholder: null
      };
    }
    return {};
  };

  return (
    <InputMask {...getInputProps()} value={value} onChange={(event) => handleChange(event.target.value)} onBlur={handleBlur}>
      {(inputProps) => (
        <TextField
          {...inputProps}
          type="text"
          error={error}
          helperText={helperText}
          fullWidth
        />
      )}
    </InputMask>
  );
};

export default InputField;
