// src/store/sortCriteriaSlice.js
import { createSlice } from '@reduxjs/toolkit';

const sortCriteriaSlice = createSlice({
  name: 'sortCriteria',
  initialState: [],
  reducers: {
    addSortCriteria: (state, action) => {
      state.push(action.payload);
    }
  }
});

export const { addSortCriteria } = sortCriteriaSlice.actions;
export default sortCriteriaSlice.reducer;
