import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import { nanoid } from 'nanoid';
import TableRowComponent from './TableRowComponent';
import { fetchOrders, createOrder, updateOrder, deleteOrder } from '../../utils/backendApi';

const TrackingStatus = () => {
  const columns = ["Номер заказа", "Статус", "Дата отправки"];
  const initialOrder = {
    id: nanoid(),
    orderNumber: "",
    status: "",
    dateCreated: "",
    isNew: true,
    checkedStatuses: {
      isStatusChecked: false,
      isDateCreatedChecked: false
    }
  };

  const [orders, setOrders] = useState([]);
  const [filterStatus, setFilterStatus] = useState(""); // Состояние для фильтрации по статусу
  const [filterDate, setFilterDate] = useState(""); // Состояние для фильтрации по дате
  const [checkedStatus, setCheckedStatus] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const fetchedOrders = await fetchOrders();
      setOrders(fetchedOrders);
    };

    fetchData();
  }, []);

  const existingOrderNumbers = useMemo(() => orders.map(order => order.orderNumber), [orders]);

  const addRow = () => {
    setOrders([...orders, { ...initialOrder, isNew: true }]);
  };

  const saveRow = useCallback(async (data) => {
    const { id, orderNumber, status, dateCreated, isNew } = data;
    const payload = { id, orderNumber, status, dateCreated };

    if (isNew) {
      const response = await createOrder(payload);
      setOrders(response);
    } else {
      const response = await updateOrder(id, payload);
      setOrders(response);
    }
  }, [setOrders]);

  const deleteRow = useCallback(async (id, isLocal) => {
    if (isLocal) {
      setOrders(orders => orders.filter(order => order.id !== id));
    } else {
      const response = await deleteOrder(id);
      setOrders(response);
    }
  }, [setOrders]);

  const handleSortChange = (sortKey, isSorted, filterValue, id) => {
    console.log(id)
    if (sortKey === "isStatusSorted" && isSorted) {
      setFilterStatus(filterValue);
    } else if (sortKey === "isDateSorted" && isSorted) {
      setFilterDate(filterValue);
    } else {
      setFilterStatus("");
      setFilterDate("");
    }
  };

  // Фильтрация заказов на основе filterStatus и filterDate
  const filteredOrders = orders.filter(order => {
    return (!filterStatus || order.status === filterStatus) &&
           (!filterDate || order.dateCreated === filterDate);
  });

  useEffect(() => {
    if (filterStatus) {
      setCheckedStatus(prevCheckedStatus => !prevCheckedStatus);
    } else {
      setCheckedStatus(prevCheckedStatus => !prevCheckedStatus);
    }
    /*console.log(checkedStatus)*/
  }, [filterStatus]);
  
  

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map((column, index) => (
              <TableCell key={index}>{column}</TableCell>
            ))}
            <TableCell>
              <Button color="primary" variant="contained" onClick={addRow}>
                Добавить строку
              </Button>
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {filteredOrders.map((order) => (
            <TableRowComponent
              key={order.id}
              id={order.id}
              data={{ ...order, isNew: order.isNew }}
              existingOrderNumbers={existingOrderNumbers}
              onSave={saveRow}
              onDelete={(isLocal) => deleteRow(order.id, isLocal)}
              onSortChange={handleSortChange} 
              checkedStatus={checkedStatus}
            />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TrackingStatus;
