import React from 'react';
import { TextField } from '@mui/material';

const OrderNumberInput = ({ value, onChange }) => {
  const handleChange = (e) => {
    const newValue = e.target.value;
    const isValid = /^\d{0,14}$/.test(newValue); // Проверка, что ввод только чисел и не более 14 цифр
    if (isValid) {
      onChange(newValue);
    }
  };

  const isError = value.length !== 14 || !/^\d+$/.test(value);

  return (
    <TextField
      value={value}
      onChange={handleChange}
      variant="outlined"
      size="small"
      fullWidth
      placeholder="Номер заказа"
      error={isError}
      helperText={isError ? 'Номер должен быть целым положительным 14-значным числом' : ''}
    />
  );
};

export default OrderNumberInput;
