// src/store/index.js
import { configureStore } from '@reduxjs/toolkit';
import sortCriteriaReducer from './sortCriteriaSlice';

const store = configureStore({
  reducer: {
    sortCriteria: sortCriteriaReducer
  }
});

export default store;
